import{_ as t,D as s,c as r,I as e,a4 as i,j as o,o as n}from"./chunks/framework.BalxvVNQ.js";const z=JSON.parse('{"title":"Quran","titleTemplate":false,"description":"","frontmatter":{"outline":"deep","title":"Quran","titleTemplate":false},"headers":[],"relativePath":"quran/106.md","filePath":"quran/106.md"}'),l={name:"quran/106.md"},d=i('<div class="chapter-title-wrapper"><div class="chapter-title">106</div><div class="chapter-title-slovak">Kmeň Qurejšovcov</div><div class="chapter-opening">115</div><div class="chapter-opening-slovak">In the Name of Allah, the Most Compassionate, Most Merciful.</div></div><div class="intro2-wrapper"><div class="chapter-info-wrapper"><div class="chapter-info-translation">Translation</div><div class="chapter-info-name">Dr. Mustafa Khattab, The Clear Quran</div></div></div><h2 id="_1" tabindex="-1">1 <a class="header-anchor" href="#_1" aria-label="Permalink to &quot;1&quot;">​</a></h2>',3),v=i('<div><div class="main-verse"><h1 class="verse-arabic">لِإِيلَـٰفِ قُرَيْشٍ ١</h1></div><p>Pre obvyklú cestu Qurejšovcov,</p></div><div class="break"></div><h2 id="_2" tabindex="-1">2 <a class="header-anchor" href="#_2" aria-label="Permalink to &quot;2&quot;">​</a></h2>',3),c=i('<div><div class="main-verse"><h1 class="verse-arabic">إِۦلَـٰفِهِمْ رِحْلَةَ ٱلشِّتَآءِ وَٱلصَّيْفِ ٢</h1></div><p>Pre ich obvyklú cestu zimnú i letnú,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Obchodníci z kmeňa Qurejš vypravovali dve veľké obchodné karavány ročne. Jednu počas zimy do Jemenu a druhú počas leta do Šamu (bola to oblasť, ktorá sa rozprestierala približne na území dnešnej Sýrie, Libanonu, Jordánska a Palestíny spred roka 1948). Qurejšovci si počas týchto dvoch ciest prinášali všetko, čo potrebovali. Pritom ich ostatní ľudia a ostatné kmene nechávali v mieri, pretože ich pokladali za obyvateľov Mekky, v ktorej sa nachádza Kába, prvá stavba venovaná uctievaniu Boha jedného jediného, o ktorú sa sa nachádza Kába, prvá stavba venovaná uctievaniu Boha jedného jediného, o ktorú sa najvyšší vyhubil Ľudí slona (viď kapitolu Al Fíľ (slon), č. 105) a odvrátil ich zlo, ľudia si ešte viac začali vážiť Qurejšovcov, a tak ich zisky z obchodu výrazne vzrástli. Preto je táto kapitola adresovaná práve Qurejšomcom, aby si vážili bohatstvo a svoje vážené miesto medzi kmeňmi, ktoré im Boh dal a aby ďakovali Bohu za to a nasledovali posolstvo, ktoré zoslal. Kmeň Qurejšovcov bol mimo iného aj kmeňom, z ktorého pochádzal posol a prorok Muhammad (požehnanie a mier Božie nech ho sprevádzajú).</p></details><div class="break"></div><h2 id="_3" tabindex="-1">3 <a class="header-anchor" href="#_3" aria-label="Permalink to &quot;3&quot;">​</a></h2>',4),h=i('<div><div class="main-verse"><h1 class="verse-arabic">فَلْيَعْبُدُوا رَبَّ هَـٰذَا ٱلْبَيْتِ ٣</h1></div><p>Nech uctievajú Pána tohto Domu,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Jedným z pomenovaní Káby, ktorá sa nachádza v Mekke v Posvätnej mešite, je Dom alebo Starý dom. Kába bola prvá stavba na zemi, ktorá bola vybudovaná na uctievanie Boha jedného jediného.</p></details><div class="break"></div><h2 id="_4" tabindex="-1">4 <a class="header-anchor" href="#_4" aria-label="Permalink to &quot;4&quot;">​</a></h2>',4),_=o("div",null,[o("div",{class:"main-verse"},[o("h1",{class:"verse-arabic"},"ٱلَّذِىٓ أَطْعَمَهُم مِّن جُوعٍ وَءَامَنَهُم مِّنْ خَوْفٍ ٤")]),o("p",null,"Ktorý im dal najesť sa po hlade a dal im bezpečie po strachu.")],-1);function p(m,u,b,k,T,f){const a=s("Badge");return n(),r("div",null,[d,e(a,{type:"info",text:"106:1",class:"badge"}),v,e(a,{type:"info",text:"106:2",class:"badge"}),c,e(a,{type:"info",text:"106:3",class:"badge"}),h,e(a,{type:"info",text:"106:4",class:"badge"}),_])}const y=t(l,[["render",p]]);export{z as __pageData,y as default};
