import{_ as s,D as i,c as r,I as o,a4 as t,j as a,o as n}from"./chunks/framework.BalxvVNQ.js";const f=JSON.parse('{"title":"Quran","titleTemplate":false,"description":"","frontmatter":{"outline":"deep","title":"Quran","titleTemplate":false},"headers":[],"relativePath":"quran/108.md","filePath":"quran/108.md"}'),d={name:"quran/108.md"},l=t('<div class="chapter-title-wrapper"><div class="chapter-title">108</div><div class="chapter-title-slovak">Rajská rieka</div><div class="chapter-opening">115</div><div class="chapter-opening-slovak">In the Name of Allah, the Most Compassionate, Most Merciful.</div></div><div class="intro2-wrapper"><div class="chapter-info-wrapper"><div class="chapter-info-translation">Translation</div><div class="chapter-info-name">Dr. Mustafa Khattab, The Clear Quran</div></div></div><h2 id="_1" tabindex="-1">1 <a class="header-anchor" href="#_1" aria-label="Permalink to &quot;1&quot;">​</a></h2>',3),v=t('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّآ أَعْطَيْنَـٰكَ ٱلْكَوْثَرَ ١</h1></div><p>My sme ti dali Al Kawsar,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Slovo Al Kawsar na tomto mieste znamená rajskú rieku, ktorá predstavuje hojné dobro. Prorokovi Muhammadovi (p.) Boh najvyšší túto rieku sľúbil v Koráne. Brehy rieky Al Kawsar sú zo zlata, dno z drahokamov, jej zem je voňavejšia než misk (druh prírodných aróm používaných na výrobu parfumov), jej voda je sladšia než med a biela ako sneh. Kto sa z tejto rieky raz napije, už nikdy viac nepocíti smäd.</p></details><div class="break"></div><h2 id="_2" tabindex="-1">2 <a class="header-anchor" href="#_2" aria-label="Permalink to &quot;2&quot;">​</a></h2>',4),m=t('<div><div class="main-verse"><h1 class="verse-arabic">فَصَلِّ لِرَبِّكَ وَٱنْحَرْ ٢</h1></div><p>Modli sa preto k Pánovi svojmu a obetuj.</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Je to príkaz adresovaný ľuďom k uznaniu jedinosti Boha, a to modlením sa a obetovaním len Bohu najvyššiemu. Príkaz modliť sa a obetovať len Bohu bol vyslovený najmä preto, že ľudia sa zvyčajne modlia a niekomu obetujú obete ako prejav najväčšieho uznania. Preto by sa muslim mal modliť len k Bohu a keď by obetoval, tak obetovať len pre Boha. Obetovanie v islame sa koná s určeným druhom zvierat, ktorých konzumácia nie je zakázaná. Medzi takéto zvieratá sa zaraďujú barany, kravy, ťavy a pod. Zviera musí spĺňať niekoľko podmienok, medzi ktoré napríklad patrí, že zviera nesmie byť choré a ani postihnuté. Mäso obetovaných zvierat sa potom má rozdať chudobným a tým, ktorí to potrebujú a v poradí, aké na to Korán určil.</p></details><div class="break"></div><h2 id="_3" tabindex="-1">3 <a class="header-anchor" href="#_3" aria-label="Permalink to &quot;3&quot;">​</a></h2>',4),c=a("div",null,[a("div",{class:"main-verse"},[a("h1",{class:"verse-arabic"},"إِنَّ شَانِئَكَ هُوَ ٱلْأَبْتَرُ ٣")]),a("p",null,"Ten, kto ťa neznáša, je ten, kto je odrezaný od akéhokoľvek dobra.")],-1),p=a("details",{class:"details custom-block"},[a("summary",null,"Tafsir"),a("p",null,"Keď zomrel Al Qásem, syn proroka Muhammada, Al Ás Bin Wáeľ povedal: „Nechajte ho (Muhammada), veď je to muž odrezaný od akéhokoľvek dobra, nemá totiž žiadneho potomka a až zomrie, upadne do zabudnutia“. Arabi pred islamom používali výraz „odrezaný“ pre pomenovanie muža, ktorý nemal žiadnych mužských potomkov. Vtedy Boh najvyšší zoslal túto kapitolu (súru), v ktorej vysvetľuje, že muž odrezaný od dobra nie je ten, kto potomkov nemá, ale ten, kto odmieta nasledovať to, čo Boh v Koráne zoslal. Je to ten, koho záujem je sústredený len na život pozemský. V súdny deň preto takýto človek príde v stave odmietania viery a bez dobrých činov a skutkov.")],-1);function u(h,k,_,b,j,z){const e=i("Badge");return n(),r("div",null,[l,o(e,{type:"info",text:"108:1",class:"badge"}),v,o(e,{type:"info",text:"108:2",class:"badge"}),m,o(e,{type:"info",text:"108:3",class:"badge"}),c,p])}const T=s(d,[["render",u]]);export{f as __pageData,T as default};
