import{_ as o,D as _,c as d,I as e,a2 as i,j as s,o as t}from"./chunks/framework.-9P5VKzf.js";const ka=JSON.parse('{"title":"","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"quran/41.md","filePath":"quran/41.md"}'),r={name:"quran/41.md"},v=i('<div class="chapter-title-wrapper"><div class="chapter-title">41</div><div class="chapter-title-slovak">Objasnená (a vysvetlená)</div><div class="chapter-opening">115</div><div class="chapter-opening-slovak">In the Name of Allah, the Most Compassionate, Most Merciful.</div></div><div class="intro2-wrapper"><div class="chapter-info-wrapper"><div class="chapter-info-translation">Translation</div><div class="chapter-info-name">Dr. Mustafa Khattab, The Clear Quran</div></div></div><h2 id="_1" tabindex="-1">1 <a class="header-anchor" href="#_1" aria-label="Permalink to &quot;1&quot;">​</a></h2>',3),n=i('<div><div class="main-verse"><h1 class="verse-arabic">حمٓ ١</h1></div><p>Há, Mím.</p></div><div class="break"></div><h2 id="_2" tabindex="-1">2 <a class="header-anchor" href="#_2" aria-label="Permalink to &quot;2&quot;">​</a></h2>',3),l=i('<div><div class="main-verse"><h1 class="verse-arabic">تَنزِيلٌ مِّنَ ٱلرَّحْمَـٰنِ ٱلرَّحِيمِ ٢</h1></div><p>Je zoslaná od Milostivého, v moci ktorého je milosť,</p></div><div class="break"></div><h2 id="_3" tabindex="-1">3 <a class="header-anchor" href="#_3" aria-label="Permalink to &quot;3&quot;">​</a></h2>',3),h=i('<div><div class="main-verse"><h1 class="verse-arabic">كِتَـٰبٌ فُصِّلَتْ ءَايَـٰتُهُۥ قُرْءَانًا عَرَبِيًّا لِّقَوْمٍ يَعْلَمُونَ ٣</h1></div><p>Kniha, ktorej znamenia boli objasnené, v podobe Koránu v arabskom jazyku ľuďom, ktorí vedia.</p></div><div class="break"></div><h2 id="_4" tabindex="-1">4 <a class="header-anchor" href="#_4" aria-label="Permalink to &quot;4&quot;">​</a></h2>',3),c=i('<div><div class="main-verse"><h1 class="verse-arabic">بَشِيرًا وَنَذِيرًا فَأَعْرَضَ أَكْثَرُهُمْ فَهُمْ لَا يَسْمَعُونَ ٤</h1></div><p>Je zvestovateľkou radostnej zvesti a varovateľkou. Väčšina z nich sa ale odvrátila a nepočúva.</p></div><div class="break"></div><h2 id="_5" tabindex="-1">5 <a class="header-anchor" href="#_5" aria-label="Permalink to &quot;5&quot;">​</a></h2>',3),b=i('<div><div class="main-verse"><h1 class="verse-arabic">وَقَالُوا قُلُوبُنَا فِىٓ أَكِنَّةٍ مِّمَّا تَدْعُونَآ إِلَيْهِ وَفِىٓ ءَاذَانِنَا وَقْرٌ وَمِنۢ بَيْنِنَا وَبَيْنِكَ حِجَابٌ فَٱعْمَلْ إِنَّنَا عَـٰمِلُونَ ٥</h1></div><p>Povedali: „Naše srdcia sú zahalené rúškom voči tomu, k čomu nás vyzývaš a v našich ušiach je hluchota a medzi nami a tebou je závoj. Ty konaj po svojom a aj my budeme konať po svojom.“</p></div><div class="break"></div><h2 id="_6" tabindex="-1">6 <a class="header-anchor" href="#_6" aria-label="Permalink to &quot;6&quot;">​</a></h2>',3),p=i('<div><div class="main-verse"><h1 class="verse-arabic">قُلْ إِنَّمَآ أَنَا۠ بَشَرٌ مِّثْلُكُمْ يُوحَىٰٓ إِلَىَّ أَنَّمَآ إِلَـٰهُكُمْ إِلَـٰهٌ وَٰحِدٌ فَٱسْتَقِيمُوٓا إِلَيْهِ وَٱسْتَغْفِرُوهُ ۗ وَوَيْلٌ لِّلْمُشْرِكِينَ ٦</h1></div><p>Povedz: „Ja som len človek ako vy, ktorému je zvestované, že vaším bohom je jeden jediný Boh. Buďte preto k Nemu priami a proste Ho o odpustenie.“ Beda tým, ktorí pridružujú k Bohu niečo alebo niekoho,</p></div><div class="break"></div><h2 id="_7" tabindex="-1">7 <a class="header-anchor" href="#_7" aria-label="Permalink to &quot;7&quot;">​</a></h2>',3),m=i('<div><div class="main-verse"><h1 class="verse-arabic">ٱلَّذِينَ لَا يُؤْتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلْـَٔاخِرَةِ هُمْ كَـٰفِرُونَ ٧</h1></div><p>Ktorí nedávajú zakat a ktorí v život posledný odmietajú veriť.</p></div><div class="break"></div><h2 id="_8" tabindex="-1">8 <a class="header-anchor" href="#_8" aria-label="Permalink to &quot;8&quot;">​</a></h2>',3),T=i('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ ٱلَّذِينَ ءَامَنُوا وَعَمِلُوا ٱلصَّـٰلِحَـٰتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ ٨</h1></div><p>Tí, ktorí uverili a dobré skutky konali, tým sa dostane odmena nekonečná.</p></div><div class="break"></div><h2 id="_9" tabindex="-1">9 <a class="header-anchor" href="#_9" aria-label="Permalink to &quot;9&quot;">​</a></h2>',3),k=i('<div><div class="main-verse"><h1 class="verse-arabic">۞ قُلْ أَئِنَّكُمْ لَتَكْفُرُونَ بِٱلَّذِى خَلَقَ ٱلْأَرْضَ فِى يَوْمَيْنِ وَتَجْعَلُونَ لَهُۥٓ أَندَادًا ۚ ذَٰلِكَ رَبُّ ٱلْعَـٰلَمِينَ ٩</h1></div><p>Povedz: „Veď vy odmietate veriť v Toho, ktorý stvoril Zem za dva dni a činíte mu rovných. Ten je Pán tvorstva.“</p></div><div class="break"></div><h2 id="_10" tabindex="-1">10 <a class="header-anchor" href="#_10" aria-label="Permalink to &quot;10&quot;">​</a></h2>',3),u=i('<div><div class="main-verse"><h1 class="verse-arabic">وَجَعَلَ فِيهَا رَوَٰسِىَ مِن فَوْقِهَا وَبَـٰرَكَ فِيهَا وَقَدَّرَ فِيهَآ أَقْوَٰتَهَا فِىٓ أَرْبَعَةِ أَيَّامٍ سَوَآءً لِّلسَّآئِلِينَ ١٠</h1></div><p>Učinil jej kotvy na nej a požehnal jej a určil jej obživu v štyroch dňoch rovných. Je to odpoveď pre tých, ktorí sa pýtajú,</p></div><div class="break"></div><h2 id="_11" tabindex="-1">11 <a class="header-anchor" href="#_11" aria-label="Permalink to &quot;11&quot;">​</a></h2>',3),P=i('<div><div class="main-verse"><h1 class="verse-arabic">ثُمَّ ٱسْتَوَىٰٓ إِلَى ٱلسَّمَآءِ وَهِىَ دُخَانٌ فَقَالَ لَهَا وَلِلْأَرْضِ ٱئْتِيَا طَوْعًا أَوْ كَرْهًا قَالَتَآ أَتَيْنَا طَآئِعِينَ ١١</h1></div><p>Potom do neba istavá, ktoré bolo dymom a povedal mu i Zemi: „Príďte dobrovoľne alebo proti svojej vôli“. Povedali: „Prichádzame dobrovoľne“.</p></div><div class="break"></div><h2 id="_12" tabindex="-1">12 <a class="header-anchor" href="#_12" aria-label="Permalink to &quot;12&quot;">​</a></h2>',3),A=i('<div><div class="main-verse"><h1 class="verse-arabic">فَقَضَىٰهُنَّ سَبْعَ سَمَـٰوَاتٍ فِى يَوْمَيْنِ وَأَوْحَىٰ فِى كُلِّ سَمَآءٍ أَمْرَهَا ۚ وَزَيَّنَّا ٱلسَّمَآءَ ٱلدُّنْيَا بِمَصَـٰبِيحَ وَحِفْظًا ۚ ذَٰلِكَ تَقْدِيرُ ٱلْعَزِيزِ ٱلْعَلِيمِ ١٢</h1></div><p>A tak ich ako sedem nebies dokončil v dvoch dňoch a vnukol v každom nebi vec jeho. A vyzdobili sme dolné nebo lampami i preto, aby strážili. To je ustanovenie Mocného a Vševediaceho.</p></div><div class="break"></div><h2 id="_13" tabindex="-1">13 <a class="header-anchor" href="#_13" aria-label="Permalink to &quot;13&quot;">​</a></h2>',3),S=i('<div><div class="main-verse"><h1 class="verse-arabic">فَإِنْ أَعْرَضُوا فَقُلْ أَنذَرْتُكُمْ صَـٰعِقَةً مِّثْلَ صَـٰعِقَةِ عَادٍ وَثَمُودَ ١٣</h1></div><p>Ak by sa odvrátili, povedz: „Varujem vás pred šokom, podobným šoku Ádovcov a Samúdovcov“,</p></div><div class="break"></div><h2 id="_14" tabindex="-1">14 <a class="header-anchor" href="#_14" aria-label="Permalink to &quot;14&quot;">​</a></h2>',3),y=i('<div><div class="main-verse"><h1 class="verse-arabic">إِذْ جَآءَتْهُمُ ٱلرُّسُلُ مِنۢ بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ أَلَّا تَعْبُدُوٓا إِلَّا ٱللَّهَ ۖ قَالُوا لَوْ شَآءَ رَبُّنَا لَأَنزَلَ مَلَـٰٓئِكَةً فَإِنَّا بِمَآ أُرْسِلْتُم بِهِۦ كَـٰفِرُونَ ١٤</h1></div><p>„Keď k nim prišli ich poslovia spredu i zozadu: „Neuctievajte nikoho iného než Boha“. Povedali: „Keby bol náš Pán chcel, bol by zoslal dole anjelov. My v to, s čím ste boli poslaní, odmietame veriť“.“</p></div><div class="break"></div><h2 id="_15" tabindex="-1">15 <a class="header-anchor" href="#_15" aria-label="Permalink to &quot;15&quot;">​</a></h2>',3),f=i('<div><div class="main-verse"><h1 class="verse-arabic">فَأَمَّا عَادٌ فَٱسْتَكْبَرُوا فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ وَقَالُوا مَنْ أَشَدُّ مِنَّا قُوَّةً ۖ أَوَلَمْ يَرَوْا أَنَّ ٱللَّهَ ٱلَّذِى خَلَقَهُمْ هُوَ أَشَدُّ مِنْهُمْ قُوَّةً ۖ وَكَانُوا بِـَٔايَـٰتِنَا يَجْحَدُونَ ١٥</h1></div><p>Ádovci, tí sa povyšovali na zemi bezprávne a povedali: „Kto má väčšiu silu než my?“ Nevideli, že Boh, ktorý ich stvoril, má väčšiu silu než oni? A naše znamenia popierali.</p></div><div class="break"></div><h2 id="_16" tabindex="-1">16 <a class="header-anchor" href="#_16" aria-label="Permalink to &quot;16&quot;">​</a></h2>',3),V=i('<div><div class="main-verse"><h1 class="verse-arabic">فَأَرْسَلْنَا عَلَيْهِمْ رِيحًا صَرْصَرًا فِىٓ أَيَّامٍ نَّحِسَاتٍ لِّنُذِيقَهُمْ عَذَابَ ٱلْخِزْىِ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا ۖ وَلَعَذَابُ ٱلْـَٔاخِرَةِ أَخْزَىٰ ۖ وَهُمْ لَا يُنصَرُونَ ١٦</h1></div><p>A tak sme na nich poslali vietor svišťavý počas dní nešťastných, aby sme im dali okúsiť trápenie poníženia v živote najnižšom. Veru, trápenie života posledného je potupnejšie. Nebude im daná podpora a pomoc.</p></div><div class="break"></div><h2 id="_17" tabindex="-1">17 <a class="header-anchor" href="#_17" aria-label="Permalink to &quot;17&quot;">​</a></h2>',3),q=i('<div><div class="main-verse"><h1 class="verse-arabic">وَأَمَّا ثَمُودُ فَهَدَيْنَـٰهُمْ فَٱسْتَحَبُّوا ٱلْعَمَىٰ عَلَى ٱلْهُدَىٰ فَأَخَذَتْهُمْ صَـٰعِقَةُ ٱلْعَذَابِ ٱلْهُونِ بِمَا كَانُوا يَكْسِبُونَ ١٧</h1></div><p>Samúdovcov, tých sme správne usmernili, ale bola im milšia slepota pred správnym usmernením, preto ich zasiahol šok trápenia potupného za to, čo získavali.</p></div><div class="break"></div><h2 id="_18" tabindex="-1">18 <a class="header-anchor" href="#_18" aria-label="Permalink to &quot;18&quot;">​</a></h2>',3),x=i('<div><div class="main-verse"><h1 class="verse-arabic">وَنَجَّيْنَا ٱلَّذِينَ ءَامَنُوا وَكَانُوا يَتَّقُونَ ١٨</h1></div><p>A zachránili sme tých, ktorí uverili a bohabojnými boli.</p></div><div class="break"></div><h2 id="_19" tabindex="-1">19 <a class="header-anchor" href="#_19" aria-label="Permalink to &quot;19&quot;">​</a></h2>',3),C=i('<div><div class="main-verse"><h1 class="verse-arabic">وَيَوْمَ يُحْشَرُ أَعْدَآءُ ٱللَّهِ إِلَى ٱلنَّارِ فَهُمْ يُوزَعُونَ ١٩</h1></div><p>V deň, keď budú zhromaždení nepriatelia Boha do ohňa, oni budú usporiadaní,</p></div><div class="break"></div><h2 id="_20" tabindex="-1">20 <a class="header-anchor" href="#_20" aria-label="Permalink to &quot;20&quot;">​</a></h2>',3),I=i('<div><div class="main-verse"><h1 class="verse-arabic">حَتَّىٰٓ إِذَا مَا جَآءُوهَا شَهِدَ عَلَيْهِمْ سَمْعُهُمْ وَأَبْصَـٰرُهُمْ وَجُلُودُهُم بِمَا كَانُوا يَعْمَلُونَ ٢٠</h1></div><p>Až tam prídu, bude o nich svedčiť ich sluch, zrak a koža o tom, čo konali,</p></div><div class="break"></div><h2 id="_21" tabindex="-1">21 <a class="header-anchor" href="#_21" aria-label="Permalink to &quot;21&quot;">​</a></h2>',3),j=i('<div><div class="main-verse"><h1 class="verse-arabic">وَقَالُوا لِجُلُودِهِمْ لِمَ شَهِدتُّمْ عَلَيْنَا ۖ قَالُوٓا أَنطَقَنَا ٱللَّهُ ٱلَّذِىٓ أَنطَقَ كُلَّ شَىْءٍ وَهُوَ خَلَقَكُمْ أَوَّلَ مَرَّةٍ وَإِلَيْهِ تُرْجَعُونَ ٢١</h1></div><p>Povedia svojim kožiam: „Prečo ste proti nám svedčili?“ Povedia: „Boh, ktorý dal prehovoriť všetkému, nám dal prehovoriť. On vás stvoril prvýkrát a k nemu bude váš návrat“,</p></div><div class="break"></div><h2 id="_22" tabindex="-1">22 <a class="header-anchor" href="#_22" aria-label="Permalink to &quot;22&quot;">​</a></h2>',3),z=i('<div><div class="main-verse"><h1 class="verse-arabic">وَمَا كُنتُمْ تَسْتَتِرُونَ أَن يَشْهَدَ عَلَيْكُمْ سَمْعُكُمْ وَلَآ أَبْصَـٰرُكُمْ وَلَا جُلُودُكُمْ وَلَـٰكِن ظَنَنتُمْ أَنَّ ٱللَّهَ لَا يَعْلَمُ كَثِيرًا مِّمَّا تَعْمَلُونَ ٢٢</h1></div><p>Neskrývali ste sa pred tým, aby váš sluch, zrak a koža nesvedčili o vás. Vy ste si totiž mysleli, že Boh nepozná veľa z toho, čo robíte.</p></div><div class="break"></div><h2 id="_23" tabindex="-1">23 <a class="header-anchor" href="#_23" aria-label="Permalink to &quot;23&quot;">​</a></h2>',3),N=i('<div><div class="main-verse"><h1 class="verse-arabic">وَذَٰلِكُمْ ظَنُّكُمُ ٱلَّذِى ظَنَنتُم بِرَبِّكُمْ أَرْدَىٰكُمْ فَأَصْبَحْتُم مِّنَ ٱلْخَـٰسِرِينَ ٢٣</h1></div><p>To boli vaše myšlienky, ktoré ste si mysleli o vašom Pánovi, ony spôsobili, že ste do záhuby skĺzli, a tak ste sa dostali medzi tých, ktorí stratu utrpeli.</p></div><div class="break"></div><h2 id="_24" tabindex="-1">24 <a class="header-anchor" href="#_24" aria-label="Permalink to &quot;24&quot;">​</a></h2>',3),D=i('<div><div class="main-verse"><h1 class="verse-arabic">فَإِن يَصْبِرُوا فَٱلنَّارُ مَثْوًى لَّهُمْ ۖ وَإِن يَسْتَعْتِبُوا فَمَا هُم مِّنَ ٱلْمُعْتَبِينَ ٢٤</h1></div><p>Ak budú trpezliví, oheň bude ich obydlím. A ak by žiadali, aby im to bolo vyčítané, nič im vyčítané nebude.</p></div><div class="break"></div><h2 id="_25" tabindex="-1">25 <a class="header-anchor" href="#_25" aria-label="Permalink to &quot;25&quot;">​</a></h2>',3),g=i('<div><div class="main-verse"><h1 class="verse-arabic">۞ وَقَيَّضْنَا لَهُمْ قُرَنَآءَ فَزَيَّنُوا لَهُم مَّا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَحَقَّ عَلَيْهِمُ ٱلْقَوْلُ فِىٓ أُمَمٍ قَدْ خَلَتْ مِن قَبْلِهِم مِّنَ ٱلْجِنِّ وَٱلْإِنسِ ۖ إِنَّهُمْ كَانُوا خَـٰسِرِينَ ٢٥</h1></div><p>Pridelili sme im priateľov, ktorí im okrášlili ich súčasnosť i budúcnosť a právom sa uplatnili voči nim slová, ktoré sa uplatnili voči spoločenstvám z radov žinnov a ľudí, ktoré boli pred nimi. Oni patrili medzi tých, ktorí prehru utrpeli.</p></div><div class="break"></div><h2 id="_26" tabindex="-1">26 <a class="header-anchor" href="#_26" aria-label="Permalink to &quot;26&quot;">​</a></h2>',3),E=i('<div><div class="main-verse"><h1 class="verse-arabic">وَقَالَ ٱلَّذِينَ كَفَرُوا لَا تَسْمَعُوا لِهَـٰذَا ٱلْقُرْءَانِ وَٱلْغَوْا فِيهِ لَعَلَّكُمْ تَغْلِبُونَ ٢٦</h1></div><p>Tí, ktorí odmietli veriť, povedali: „Nepočúvajte tento Korán a prehlušujte ho planými rečami, azda premôžete.“</p></div><div class="break"></div><h2 id="_27" tabindex="-1">27 <a class="header-anchor" href="#_27" aria-label="Permalink to &quot;27&quot;">​</a></h2>',3),R=i('<div><div class="main-verse"><h1 class="verse-arabic">فَلَنُذِيقَنَّ ٱلَّذِينَ كَفَرُوا عَذَابًا شَدِيدًا وَلَنَجْزِيَنَّهُمْ أَسْوَأَ ٱلَّذِى كَانُوا يَعْمَلُونَ ٢٧</h1></div><p>Dáme okúsiť tým, ktorí odmietli veriť, trápenie prísne a odplatíme im to najhoršie z toho, čo konali.</p></div><div class="break"></div><h2 id="_28" tabindex="-1">28 <a class="header-anchor" href="#_28" aria-label="Permalink to &quot;28&quot;">​</a></h2>',3),B=i('<div><div class="main-verse"><h1 class="verse-arabic">ذَٰلِكَ جَزَآءُ أَعْدَآءِ ٱللَّهِ ٱلنَّارُ ۖ لَهُمْ فِيهَا دَارُ ٱلْخُلْدِ ۖ جَزَآءًۢ بِمَا كَانُوا بِـَٔايَـٰتِنَا يَجْحَدُونَ ٢٨</h1></div><p>To je odplata nepriateľom Boha, oheň. Budú v ňom mať príbytok večný. Odplata za to, že naše znamenia popierali.</p></div><div class="break"></div><h2 id="_29" tabindex="-1">29 <a class="header-anchor" href="#_29" aria-label="Permalink to &quot;29&quot;">​</a></h2>',3),K=i('<div><div class="main-verse"><h1 class="verse-arabic">وَقَالَ ٱلَّذِينَ كَفَرُوا رَبَّنَآ أَرِنَا ٱلَّذَيْنِ أَضَلَّانَا مِنَ ٱلْجِنِّ وَٱلْإِنسِ نَجْعَلْهُمَا تَحْتَ أَقْدَامِنَا لِيَكُونَا مِنَ ٱلْأَسْفَلِينَ ٢٩</h1></div><p>Tí, ktorí odmietli veriť, povedali: „Pane náš, ukáž nám tých žinnov a ľudí, ktorí nás do bludu uviedli, aby sme ich učinili pod našimi nohami, aby patrili medzi tých najnižších“.</p></div><div class="break"></div><h2 id="_30" tabindex="-1">30 <a class="header-anchor" href="#_30" aria-label="Permalink to &quot;30&quot;">​</a></h2>',3),O=i('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ ٱلَّذِينَ قَالُوا رَبُّنَا ٱللَّهُ ثُمَّ ٱسْتَقَـٰمُوا تَتَنَزَّلُ عَلَيْهِمُ ٱلْمَلَـٰٓئِكَةُ أَلَّا تَخَافُوا وَلَا تَحْزَنُوا وَأَبْشِرُوا بِٱلْجَنَّةِ ٱلَّتِى كُنتُمْ تُوعَدُونَ ٣٠</h1></div><p>Tí, ktorí povedali: „Náš Pán je Boha“ a potom sa priamymi stali, k tým sú anjeli zosielaní: „Nebojte sa a nesmúťte a prijmite radostnú zvesť o záhrade, ktorá vám bola sľúbená“.</p></div><div class="break"></div><h2 id="_31" tabindex="-1">31 <a class="header-anchor" href="#_31" aria-label="Permalink to &quot;31&quot;">​</a></h2>',3),J=i('<div><div class="main-verse"><h1 class="verse-arabic">نَحْنُ أَوْلِيَآؤُكُمْ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا وَفِى ٱلْـَٔاخِرَةِ ۖ وَلَكُمْ فِيهَا مَا تَشْتَهِىٓ أَنفُسُكُمْ وَلَكُمْ فِيهَا مَا تَدَّعُونَ ٣١</h1></div><p>„My sme vašimi dôverníkmi v živote najnižšom a v živote poslednom. Budete v ňom mať, po čom vaše duše zatúžia a budete v ňom mať, čo si zažiadate“,</p></div><div class="break"></div><h2 id="_32" tabindex="-1">32 <a class="header-anchor" href="#_32" aria-label="Permalink to &quot;32&quot;">​</a></h2>',3),M=i('<div><div class="main-verse"><h1 class="verse-arabic">نُزُلًا مِّنْ غَفُورٍ رَّحِيمٍ ٣٢</h1></div><p>„Pohostenie od odpúšťajúceho a milostivého“.</p></div><div class="break"></div><h2 id="_33" tabindex="-1">33 <a class="header-anchor" href="#_33" aria-label="Permalink to &quot;33&quot;">​</a></h2>',3),H=i('<div><div class="main-verse"><h1 class="verse-arabic">وَمَنْ أَحْسَنُ قَوْلًا مِّمَّن دَعَآ إِلَى ٱللَّهِ وَعَمِلَ صَـٰلِحًا وَقَالَ إِنَّنِى مِنَ ٱلْمُسْلِمِينَ ٣٣</h1></div><p>Kto by lepšie slová hovoril než ten, kto vyzval k Bohu a konal dobro a povedal: „Ja patrím medzi muslimov“.</p></div><div class="break"></div><h2 id="_34" tabindex="-1">34 <a class="header-anchor" href="#_34" aria-label="Permalink to &quot;34&quot;">​</a></h2>',3),$=i('<div><div class="main-verse"><h1 class="verse-arabic">وَلَا تَسْتَوِى ٱلْحَسَنَةُ وَلَا ٱلسَّيِّئَةُ ۚ ٱدْفَعْ بِٱلَّتِى هِىَ أَحْسَنُ فَإِذَا ٱلَّذِى بَيْنَكَ وَبَيْنَهُۥ عَدَٰوَةٌۭ كَأَنَّهُۥ وَلِىٌّ حَمِيمٌ ٣٤</h1></div><p>Dobrý skutok a zlý skutok sa nevyrovnajú. Odrážaj to zlé tým, čo je lepšie a hľa, ten, medzi ktorým a tebou bolo nepriateľstvo, sa stane akoby dôverníkom blízkym.</p></div><div class="break"></div><h2 id="_35" tabindex="-1">35 <a class="header-anchor" href="#_35" aria-label="Permalink to &quot;35&quot;">​</a></h2>',3),w=i('<div><div class="main-verse"><h1 class="verse-arabic">وَمَا يُلَقَّىٰهَآ إِلَّا ٱلَّذِينَ صَبَرُوا وَمَا يُلَقَّىٰهَآ إِلَّا ذُو حَظٍّ عَظِيمٍ ٣٥</h1></div><p>Bude dané len tým, ktorí trpezlivými boli a bude dané len tomu, kto má šťastie obrovské.</p></div><div class="break"></div><h2 id="_36" tabindex="-1">36 <a class="header-anchor" href="#_36" aria-label="Permalink to &quot;36&quot;">​</a></h2>',3),U=i('<div><div class="main-verse"><h1 class="verse-arabic">وَإِمَّا يَنزَغَنَّكَ مِنَ ٱلشَّيْطَـٰنِ نَزْغٌ فَٱسْتَعِذْ بِٱللَّهِ ۖ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلْعَلِيمُ ٣٦</h1></div><p>Ak by si cítil akékoľvek podpichnutie zo strany satana, tak žiadaj od Boha ochranu. On je Ten, kto všetko počuje a všetko vie.</p></div><div class="break"></div><h2 id="_37" tabindex="-1">37 <a class="header-anchor" href="#_37" aria-label="Permalink to &quot;37&quot;">​</a></h2>',3),Z=i('<div><div class="main-verse"><h1 class="verse-arabic">وَمِنْ ءَايَـٰتِهِ ٱلَّيْلُ وَٱلنَّهَارُ وَٱلشَّمْسُ وَٱلْقَمَرُ ۚ لَا تَسْجُدُوا لِلشَّمْسِ وَلَا لِلْقَمَرِ وَٱسْجُدُوا لِلَّهِ ٱلَّذِى خَلَقَهُنَّ إِن كُنتُمْ إِيَّاهُ تَعْبُدُونَ ٣٧</h1></div><p>K Jeho znameniam patrí noc a deň a slnko a mesiac. Neklaňajte sa do polohy sužúdu slnku a ani mesiacu, ale sa klaňajte do polohy sužúdu Bohu, ktorý ich oboje stvoril, ak Jeho skutočne uctievate.</p></div><div class="break"></div><h2 id="_38" tabindex="-1">38 <a class="header-anchor" href="#_38" aria-label="Permalink to &quot;38&quot;">​</a></h2>',3),Q=i('<div><div class="main-verse"><h1 class="verse-arabic">فَإِنِ ٱسْتَكْبَرُوا فَٱلَّذِينَ عِندَ رَبِّكَ يُسَبِّحُونَ لَهُۥ بِٱلَّيْلِ وَٱلنَّهَارِ وَهُمْ لَا يَسْـَٔمُونَ ۩ ٣٨</h1></div><p>Ak by sa povyšovali, tak tí, ktorí sú u Pána tvojho, v noci i vo dne svedčia o jedinečnosti Jeho a že sa Mu nemá pripisovať to, čo nezodpovedá pravde. Nikdy nezunujú.</p></div><div class="break"></div><h2 id="_39" tabindex="-1">39 <a class="header-anchor" href="#_39" aria-label="Permalink to &quot;39&quot;">​</a></h2>',3),F=i('<div><div class="main-verse"><h1 class="verse-arabic">وَمِنْ ءَايَـٰتِهِۦٓ أَنَّكَ تَرَى ٱلْأَرْضَ خَـٰشِعَةً فَإِذَآ أَنزَلْنَا عَلَيْهَا ٱلْمَآءَ ٱهْتَزَّتْ وَرَبَتْ ۚ إِنَّ ٱلَّذِىٓ أَحْيَاهَا لَمُحْىِ ٱلْمَوْتَىٰٓ ۚ إِنَّهُۥ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ ٣٩</h1></div><p>K Jeho znameniam patrí, že vidíš zem pokornú, keď ale na ňu zošleme vodu, zachveje sa a znásobí sa. Ten, kto ju oživil, oživí i mŕtvych. On má nad všetkým moc.</p></div><div class="break"></div><h2 id="_40" tabindex="-1">40 <a class="header-anchor" href="#_40" aria-label="Permalink to &quot;40&quot;">​</a></h2>',3),G=i('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ ٱلَّذِينَ يُلْحِدُونَ فِىٓ ءَايَـٰتِنَا لَا يَخْفَوْنَ عَلَيْنَآ ۗ أَفَمَن يُلْقَىٰ فِى ٱلنَّارِ خَيْرٌ أَم مَّن يَأْتِىٓ ءَامِنًا يَوْمَ ٱلْقِيَـٰمَةِ ۚ ٱعْمَلُوا مَا شِئْتُمْ ۖ إِنَّهُۥ بِمَا تَعْمَلُونَ بَصِيرٌ ٤٠</h1></div><p>Tí, ktorí sa neviery dopúšťajú odchýlením sa od našich znamení, nie sú pred nami skrytí. A či ten, kto bude vrhnutý do ohňa, je lepší ako ten, kto príde v bezpečí v deň zmŕtvychvstania? Konajte, čo len chcete. On vidí všetko, čo konáte.</p></div><div class="break"></div><h2 id="_41" tabindex="-1">41 <a class="header-anchor" href="#_41" aria-label="Permalink to &quot;41&quot;">​</a></h2>',3),L=i('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ ٱلَّذِينَ كَفَرُوا بِٱلذِّكْرِ لَمَّا جَآءَهُمْ ۖ وَإِنَّهُۥ لَكِتَـٰبٌ عَزِيزٌ ٤١</h1></div><p>Tí, ktorí odmietli veriť v Pripomenutie, keď k nim prišlo. Je to veru Kniha mocná.</p></div><div class="break"></div><h2 id="_42" tabindex="-1">42 <a class="header-anchor" href="#_42" aria-label="Permalink to &quot;42&quot;">​</a></h2>',3),W=i('<div><div class="main-verse"><h1 class="verse-arabic">لَّا يَأْتِيهِ ٱلْبَـٰطِلُ مِنۢ بَيْنِ يَدَيْهِ وَلَا مِنْ خَلْفِهِۦ ۖ تَنزِيلٌ مِّنْ حَكِيمٍ حَمِيدٍ ٤٢</h1></div><p>Nedostane sa k nej nepravda spredu ani zozadu. Je zoslaná od Toho, ktorý múdrosťou oplýva a ktorému vďaka patrí.</p></div><div class="break"></div><h2 id="_43" tabindex="-1">43 <a class="header-anchor" href="#_43" aria-label="Permalink to &quot;43&quot;">​</a></h2>',3),X=i('<div><div class="main-verse"><h1 class="verse-arabic">مَّا يُقَالُ لَكَ إِلَّا مَا قَدْ قِيلَ لِلرُّسُلِ مِن قَبْلِكَ ۚ إِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ وَذُو عِقَابٍ أَلِيمٍ ٤٣</h1></div><p>Je ti povedané len to, čo bolo povedané poslom pred tebou. Tvoj Pán má odpustenie a má i trest bolestivý.</p></div><div class="break"></div><h2 id="_44" tabindex="-1">44 <a class="header-anchor" href="#_44" aria-label="Permalink to &quot;44&quot;">​</a></h2>',3),Y=i('<div><div class="main-verse"><h1 class="verse-arabic">وَلَوْ جَعَلْنَـٰهُ قُرْءَانًا أَعْجَمِيًّا لَّقَالُوا لَوْلَا فُصِّلَتْ ءَايَـٰتُهُۥٓ ۖ ءَا۬عْجَمِىٌّ وَعَرَبِىٌّ ۗ قُلْ هُوَ لِلَّذِينَ ءَامَنُوا هُدًى وَشِفَآءٌ ۖ وَٱلَّذِينَ لَا يُؤْمِنُونَ فِىٓ ءَاذَانِهِمْ وَقْرٌ وَهُوَ عَلَيْهِمْ عَمًى ۚ أُولَـٰٓئِكَ يُنَادَوْنَ مِن مَّكَانٍۭ بَعِيدٍ ٤٤</h1></div><p>Keby sme ho boli učinili Korán v nearabskom jazyku, boli by povedali: „Keby boli jeho znamenia objasnené!“. Nearabský Korán a arabský posol?! Povedz: „Je pre tých, ktorí uverili, správnym usmernením a liečbou“. Tí, ktorí neveria, v ich ušiach je hluchota a on je pred ich zrakmi skrytý. Tí sú volaní z miesta ďalekého.</p></div><div class="break"></div><h2 id="_45" tabindex="-1">45 <a class="header-anchor" href="#_45" aria-label="Permalink to &quot;45&quot;">​</a></h2>',3),aa=i('<div><div class="main-verse"><h1 class="verse-arabic">وَلَقَدْ ءَاتَيْنَا مُوسَى ٱلْكِتَـٰبَ فَٱخْتُلِفَ فِيهِ ۗ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِن رَّبِّكَ لَقُضِىَ بَيْنَهُمْ ۚ وَإِنَّهُمْ لَفِى شَكٍّ مِّنْهُ مُرِيبٍ ٤٥</h1></div><p>Dali sme Mojžišovi Knihu, o nej sa však začali sporiť. Nebyť slova od Pána tvojho už predtým daného, bolo by medzi nimi rozhodnuté. Oni sú o nej na pochybách a majú podozrenie.</p></div><div class="break"></div><h2 id="_46" tabindex="-1">46 <a class="header-anchor" href="#_46" aria-label="Permalink to &quot;46&quot;">​</a></h2>',3),ea=i('<div><div class="main-verse"><h1 class="verse-arabic">مَّنْ عَمِلَ صَـٰلِحًا فَلِنَفْسِهِۦ ۖ وَمَنْ أَسَآءَ فَعَلَيْهَا ۗ وَمَا رَبُّكَ بِظَلَّـٰمٍ لِّلْعَبِيدِ ٤٦</h1></div><p>Kto konal dobro, tak pre dobro svojej duše a kto konal zlo, tak proti nej. Pán tvoj nie je krivdiaci voči poddaným.</p></div><div class="break"></div><h2 id="_47" tabindex="-1">47 <a class="header-anchor" href="#_47" aria-label="Permalink to &quot;47&quot;">​</a></h2>',3),ia=i('<div><div class="main-verse"><h1 class="verse-arabic">۞ إِلَيْهِ يُرَدُّ عِلْمُ ٱلسَّاعَةِ ۚ وَمَا تَخْرُجُ مِن ثَمَرَٰتٍ مِّنْ أَكْمَامِهَا وَمَا تَحْمِلُ مِنْ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلْمِهِۦ ۚ وَيَوْمَ يُنَادِيهِمْ أَيْنَ شُرَكَآءِى قَالُوٓا ءَاذَنَّـٰكَ مَا مِنَّا مِن شَهِيدٍ ٤٧</h1></div><p>Od neho závisí poznanie o Hodine. Žiadne plody nevyjdú von zo svojich rukávov a žiadna samica neponesie a ani neporodí bez toho, aby o tom vedel. V deň, keď na nich zavolá: „Kde sú spoločníci moji?“, povedia: „Oznamujeme Ti, že niet medzi nami na to svedka“.</p></div><div class="break"></div><h2 id="_48" tabindex="-1">48 <a class="header-anchor" href="#_48" aria-label="Permalink to &quot;48&quot;">​</a></h2>',3),sa=i('<div><div class="main-verse"><h1 class="verse-arabic">وَضَلَّ عَنْهُم مَّا كَانُوا يَدْعُونَ مِن قَبْلُ ۖ وَظَنُّوا مَا لَهُم مِّن مَّحِيصٍ ٤٨</h1></div><p>Opustí ich to, čo vzývali predtým. A pomyslia si, že už nemajú kam utiecť.</p></div><div class="break"></div><h2 id="_49" tabindex="-1">49 <a class="header-anchor" href="#_49" aria-label="Permalink to &quot;49&quot;">​</a></h2>',3),oa=i('<div><div class="main-verse"><h1 class="verse-arabic">لَّا يَسْـَٔمُ ٱلْإِنسَـٰنُ مِن دُعَآءِ ٱلْخَيْرِ وَإِن مَّسَّهُ ٱلشَّرُّ فَيَـُٔوسٌ قَنُوطٌ ٤٩</h1></div><p>Človeku sa nikdy nezunuje prosiť o dobro, ale ak by sa ho zlo dotklo, ostáva zúfalý a bez nádeje,</p></div><div class="break"></div><h2 id="_50" tabindex="-1">50 <a class="header-anchor" href="#_50" aria-label="Permalink to &quot;50&quot;">​</a></h2>',3),_a=i('<div><div class="main-verse"><h1 class="verse-arabic">وَلَئِنْ أَذَقْنَـٰهُ رَحْمَةً مِّنَّا مِنۢ بَعْدِ ضَرَّآءَ مَسَّتْهُ لَيَقُولَنَّ هَـٰذَا لِى وَمَآ أَظُنُّ ٱلسَّاعَةَ قَآئِمَةً وَلَئِن رُّجِعْتُ إِلَىٰ رَبِّىٓ إِنَّ لِى عِندَهُۥ لَلْحُسْنَىٰ ۚ فَلَنُنَبِّئَنَّ ٱلَّذِينَ كَفَرُوا بِمَا عَمِلُوا وَلَنُذِيقَنَّهُم مِّنْ عَذَابٍ غَلِيظٍ ٥٠</h1></div><p>Ak by sme mu dali okúsiť milosť našu po škode, ktorá sa ho dotkla, povie: „To mi patrí a nemyslím si, že Hodina nastane. A aj keď by som bol vrátený k Pánovi svojmu, dostane sa mi u Neho len dobro“. Tým, ktorí odmietali veriť, oznámime to, čo konali a dáme im okúsiť z trápenia veľmi silného.</p></div><div class="break"></div><h2 id="_51" tabindex="-1">51 <a class="header-anchor" href="#_51" aria-label="Permalink to &quot;51&quot;">​</a></h2>',3),da=i('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَآ أَنْعَمْنَا عَلَى ٱلْإِنسَـٰنِ أَعْرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ فَذُو دُعَآءٍ عَرِيضٍ ٥١</h1></div><p>Ak by sme človeku podarovali mnohé dary odvráti sa a vzdiali sa. Ak by sa ho ale zlo dotklo, prosby široké má.</p></div><div class="break"></div><h2 id="_52" tabindex="-1">52 <a class="header-anchor" href="#_52" aria-label="Permalink to &quot;52&quot;">​</a></h2>',3),ta=i('<div><div class="main-verse"><h1 class="verse-arabic">قُلْ أَرَءَيْتُمْ إِن كَانَ مِنْ عِندِ ٱللَّهِ ثُمَّ كَفَرْتُم بِهِۦ مَنْ أَضَلُّ مِمَّنْ هُوَ فِى شِقَاقٍۭ بَعِيدٍ ٥٢</h1></div><p>Povedz: „Vidíte, ak by pochádzal od Boha a vy by ste v neho odmietli veriť, kto už by potom vôbec mohol byť vo väčšom blude než ten, kto je v spore vzdialenom od pravdy“.</p></div><div class="break"></div><h2 id="_53" tabindex="-1">53 <a class="header-anchor" href="#_53" aria-label="Permalink to &quot;53&quot;">​</a></h2>',3),ra=i('<div><div class="main-verse"><h1 class="verse-arabic">سَنُرِيهِمْ ءَايَـٰتِنَا فِى ٱلْـَٔافَاقِ وَفِىٓ أَنفُسِهِمْ حَتَّىٰ يَتَبَيَّنَ لَهُمْ أَنَّهُ ٱلْحَقُّ ۗ أَوَلَمْ يَكْفِ بِرَبِّكَ أَنَّهُۥ عَلَىٰ كُلِّ شَىْءٍ شَهِيدٌ ٥٣</h1></div><p>Ukážeme im naše znamenia v končinách nebies a zeme a v ich dušiach, až kým sa im jasne neukáže, že je to pravda. Nepostačí, že Pán tvoj, je všetkého svedkom?</p></div><div class="break"></div><h2 id="_54" tabindex="-1">54 <a class="header-anchor" href="#_54" aria-label="Permalink to &quot;54&quot;">​</a></h2>',3),va=s("div",null,[s("div",{class:"main-verse"},[s("h1",{class:"verse-arabic"},"أَلَآ إِنَّهُمْ فِى مِرْيَةٍ مِّن لِّقَآءِ رَبِّهِمْ ۗ أَلَآ إِنَّهُۥ بِكُلِّ شَىْءٍۢ مُّحِيطٌۢ ٥٤")]),s("p",null,"Oni sú veru na pochybách o stretnutí Pána svojho. On ale veru všetko obklopil.")],-1);function na(la,ha,ca,ba,pa,ma)