import{_ as i,D as t,c as d,I as e,a2 as s,j as o,o as r}from"./chunks/framework.-9P5VKzf.js";const K=JSON.parse('{"title":"Quran","titleTemplate":false,"description":"","frontmatter":{"outline":"deep","title":"Quran","titleTemplate":false},"headers":[],"relativePath":"quran/73.md","filePath":"quran/73.md"}'),_={name:"quran/73.md"},l=s('<div class="chapter-title-wrapper"><div class="chapter-title">73</div><div class="chapter-title-slovak">Rúchom zakrytý</div><div class="chapter-opening">115</div><div class="chapter-opening-slovak">In the Name of Allah, the Most Compassionate, Most Merciful.</div></div><div class="intro2-wrapper"><div class="chapter-info-wrapper"><div class="chapter-info-translation">Translation</div><div class="chapter-info-name">Dr. Mustafa Khattab, The Clear Quran</div></div></div><h2 id="_1" tabindex="-1">1 <a class="header-anchor" href="#_1" aria-label="Permalink to &quot;1&quot;">​</a></h2>',3),n=s('<div><div class="main-verse"><h1 class="verse-arabic">يَـٰٓأَيُّهَا ٱلْمُزَّمِّلُ ١</h1></div><p>Ty rúchom zakrytý.</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Rúchom zakrytý sa myslí prorok Muhammad. Prorok je tu označený ako rúchom zakrytý v súvislosti s príhodou, ktorá sa mu prihodila v počiatkoch posolstva. Celá príhoda je uvedená vo výklade k veršu 74:5 vo vydaní prekladu Koránu s výkladom.</p></details><div class="break"></div><h2 id="_2" tabindex="-1">2 <a class="header-anchor" href="#_2" aria-label="Permalink to &quot;2&quot;">​</a></h2>',4),v=s('<div><div class="main-verse"><h1 class="verse-arabic">قُمِ ٱلَّيْلَ إِلَّا قَلِيلًا ٢</h1></div><p>Ostaň hore v noci modliac sa, okrem malej časti z nej,</p></div><div class="break"></div><h2 id="_3" tabindex="-1">3 <a class="header-anchor" href="#_3" aria-label="Permalink to &quot;3&quot;">​</a></h2>',3),c=s('<div><div class="main-verse"><h1 class="verse-arabic">نِّصْفَهُۥٓ أَوِ ٱنقُصْ مِنْهُ قَلِيلًا ٣</h1></div><p>Polovicu z nej, alebo uber z nej trocha,</p></div><div class="break"></div><h2 id="_4" tabindex="-1">4 <a class="header-anchor" href="#_4" aria-label="Permalink to &quot;4&quot;">​</a></h2>',3),h=s('<div><div class="main-verse"><h1 class="verse-arabic">أَوْ زِدْ عَلَيْهِ وَرَتِّلِ ٱلْقُرْءَانَ تَرْتِيلًا ٤</h1></div><p>Alebo pridaj k nej a odriekaj Korán odriekaním pomalým.</p></div><div class="break"></div><h2 id="_5" tabindex="-1">5 <a class="header-anchor" href="#_5" aria-label="Permalink to &quot;5&quot;">​</a></h2>',3),p=s('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّا سَنُلْقِى عَلَيْكَ قَوْلًا ثَقِيلًا ٥</h1></div><p>My vrhneme na teba slová ťažké,</p></div><div class="break"></div><h2 id="_6" tabindex="-1">6 <a class="header-anchor" href="#_6" aria-label="Permalink to &quot;6&quot;">​</a></h2>',3),m=s('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ نَاشِئَةَ ٱلَّيْلِ هِىَ أَشَدُّ وَطْـًٔا وَأَقْوَمُ قِيلًا ٦</h1></div><p>Prvé hodiny noci, tie majú väčší dopad a povedia sa v nich správnejšie slová,</p></div><div class="break"></div><h2 id="_7" tabindex="-1">7 <a class="header-anchor" href="#_7" aria-label="Permalink to &quot;7&quot;">​</a></h2>',3),u=s('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ لَكَ فِى ٱلنَّهَارِ سَبْحًا طَوِيلًا ٧</h1></div><p>Vo dne sa o záležitosti svoje dlho staráš,</p></div><div class="break"></div><h2 id="_8" tabindex="-1">8 <a class="header-anchor" href="#_8" aria-label="Permalink to &quot;8&quot;">​</a></h2>',3),b=s('<div><div class="main-verse"><h1 class="verse-arabic">وَٱذْكُرِ ٱسْمَ رَبِّكَ وَتَبَتَّلْ إِلَيْهِ تَبْتِيلًا ٨</h1></div><p>Ale spomínaj meno Pána svojho a k Nemu sa obráť obrátením úplným,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Význam predchádzajúcich veršov je: Až dokončíš svoje každodenné povinnosti, Muhammad, noc príde a všetko sa uloží k spánku. Potom sa už celým svojim srdcom v časti noci venuj už len Bohu, jeho uctievaniu a prednášaniu Koránu.</p></details><div class="break"></div><h2 id="_9" tabindex="-1">9 <a class="header-anchor" href="#_9" aria-label="Permalink to &quot;9&quot;">​</a></h2>',4),T=s('<div><div class="main-verse"><h1 class="verse-arabic">رَّبُّ ٱلْمَشْرِقِ وَٱلْمَغْرِبِ لَآ إِلَـٰهَ إِلَّا هُوَ فَٱتَّخِذْهُ وَكِيلًا ٩</h1></div><p>K Pánovi východu a západu. Niet boha okrem Neho, preto sa naňho spoľahni,</p></div><div class="break"></div><h2 id="_10" tabindex="-1">10 <a class="header-anchor" href="#_10" aria-label="Permalink to &quot;10&quot;">​</a></h2>',3),k=s('<div><div class="main-verse"><h1 class="verse-arabic">وَٱصْبِرْ عَلَىٰ مَا يَقُولُونَ وَٱهْجُرْهُمْ هَجْرًا جَمِيلًا ١٠</h1></div><p>Buď trpezlivý voči tomu, čo hovoria a opusti ich opustením pekným,</p></div><div class="break"></div><h2 id="_11" tabindex="-1">11 <a class="header-anchor" href="#_11" aria-label="Permalink to &quot;11&quot;">​</a></h2>',3),P=s('<div><div class="main-verse"><h1 class="verse-arabic">وَذَرْنِى وَٱلْمُكَذِّبِينَ أُولِى ٱلنَّعْمَةِ وَمَهِّلْهُمْ قَلِيلًا ١١</h1></div><p>Nechaj Ma s tými, ktorí za lož označujú, ktorým sa dary dostali a počkaj na nich trocha,</p></div><div class="break"></div><h2 id="_12" tabindex="-1">12 <a class="header-anchor" href="#_12" aria-label="Permalink to &quot;12&quot;">​</a></h2>',3),A=s('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ لَدَيْنَآ أَنكَالًا وَجَحِيمًا ١٢</h1></div><p>U nás sú okovy a peklo</p></div><div class="break"></div><h2 id="_13" tabindex="-1">13 <a class="header-anchor" href="#_13" aria-label="Permalink to &quot;13&quot;">​</a></h2>',3),y=s('<div><div class="main-verse"><h1 class="verse-arabic">وَطَعَامًا ذَا غُصَّةٍ وَعَذَابًا أَلِيمًا ١٣</h1></div><p>A jedlo, ktoré dusí i trápenie bolestivé.</p></div><div class="break"></div><h2 id="_14" tabindex="-1">14 <a class="header-anchor" href="#_14" aria-label="Permalink to &quot;14&quot;">​</a></h2>',3),S=s('<div><div class="main-verse"><h1 class="verse-arabic">يَوْمَ تَرْجُفُ ٱلْأَرْضُ وَٱلْجِبَالُ وَكَانَتِ ٱلْجِبَالُ كَثِيبًا مَّهِيلًا ١٤</h1></div><p>V deň, keď sa zem a hory otrasú a hory ostanú ako pohyblivý piesok.</p></div><div class="break"></div><h2 id="_15" tabindex="-1">15 <a class="header-anchor" href="#_15" aria-label="Permalink to &quot;15&quot;">​</a></h2>',3),f=s('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّآ أَرْسَلْنَآ إِلَيْكُمْ رَسُولًا شَـٰهِدًا عَلَيْكُمْ كَمَآ أَرْسَلْنَآ إِلَىٰ فِرْعَوْنَ رَسُولًا ١٥</h1></div><p>My sme k vám poslali posla ako svedka svedčiaceho o vás, podobne ako sme poslali k Faraónovi posla,</p></div><div class="break"></div><h2 id="_16" tabindex="-1">16 <a class="header-anchor" href="#_16" aria-label="Permalink to &quot;16&quot;">​</a></h2>',3),j=s('<div><div class="main-verse"><h1 class="verse-arabic">فَعَصَىٰ فِرْعَوْنُ ٱلرَّسُولَ فَأَخَذْنَـٰهُ أَخْذًا وَبِيلًا ١٦</h1></div><p>Faraón však neposlúchol posla, preto sme ho vzali vzatím so zlým následkom preňho.</p></div><div class="break"></div><h2 id="_17" tabindex="-1">17 <a class="header-anchor" href="#_17" aria-label="Permalink to &quot;17&quot;">​</a></h2>',3),V=s('<div><div class="main-verse"><h1 class="verse-arabic">فَكَيْفَ تَتَّقُونَ إِن كَفَرْتُمْ يَوْمًا يَجْعَلُ ٱلْوِلْدَٰنَ شِيبًا ١٧</h1></div><p>Pokiaľ by ste odmietli veriť, ako sa vyhnete dňu, ktorý učiní deti šedivými?</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Adresátmi sú tu tí z kmeňa Qurejš, z obyvateľov Mekky, ktorí odmietali veriť v posolstvo, ktoré Boh prostredníctvom svojho posla Muhammada ľuďom zoslal.</p></details><div class="break"></div><h2 id="_18" tabindex="-1">18 <a class="header-anchor" href="#_18" aria-label="Permalink to &quot;18&quot;">​</a></h2>',4),C=s('<div><div class="main-verse"><h1 class="verse-arabic">ٱلسَّمَآءُ مُنفَطِرٌۢ بِهِۦ ۚ كَانَ وَعْدُهُۥ مَفْعُولًا ١٨</h1></div><p>Nebo bude roztrhnuté v ňom. Jeho sľub sa naplnil.</p></div><div class="break"></div><h2 id="_19" tabindex="-1">19 <a class="header-anchor" href="#_19" aria-label="Permalink to &quot;19&quot;">​</a></h2>',3),q=s('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ هَـٰذِهِۦ تَذْكِرَةٌ ۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلًا ١٩</h1></div><p>Toto je pripomenutie. Kto bude chcieť, ten na cestu k Pánovi svojmu vykročí.</p></div><div class="break"></div><h2 id="_20" tabindex="-1">20 <a class="header-anchor" href="#_20" aria-label="Permalink to &quot;20&quot;">​</a></h2>',3),x=o("div",null,[o("div",{class:"main-verse"},[o("h1",{class:"verse-arabic"},"۞ إِنَّ رَبَّكَ يَعْلَمُ أَنَّكَ تَقُومُ أَدْنَىٰ مِن ثُلُثَىِ ٱلَّيْلِ وَنِصْفَهُۥ وَثُلُثَهُۥ وَطَآئِفَةٌ مِّنَ ٱلَّذِينَ مَعَكَ ۚ وَٱللَّهُ يُقَدِّرُ ٱلَّيْلَ وَٱلنَّهَارَ ۚ عَلِمَ أَن لَّن تُحْصُوهُ فَتَابَ عَلَيْكُمْ ۖ فَٱقْرَءُوا مَا تَيَسَّرَ مِنَ ٱلْقُرْءَانِ ۚ عَلِمَ أَن سَيَكُونُ مِنكُم مَّرْضَىٰ ۙ وَءَاخَرُونَ يَضْرِبُونَ فِى ٱلْأَرْضِ يَبْتَغُونَ مِن فَضْلِ ٱللَّهِ ۙ وَءَاخَرُونَ يُقَـٰتِلُونَ فِى سَبِيلِ ٱللَّهِ ۖ فَٱقْرَءُوا مَا تَيَسَّرَ مِنْهُ ۚ وَأَقِيمُوا ٱلصَّلَوٰةَ وَءَاتُوا ٱلزَّكَوٰةَ وَأَقْرِضُوا ٱللَّهَ قَرْضًا حَسَنًا ۚ وَمَا تُقَدِّمُوا لِأَنفُسِكُم مِّنْ خَيْرٍ تَجِدُوهُ عِندَ ٱللَّهِ هُوَ خَيْرًا وَأَعْظَمَ أَجْرًا ۚ وَٱسْتَغْفِرُوا ٱللَّهَ ۖ إِنَّ ٱللَّهَ غَفُورٌ رَّحِيمٌ ٢٠")]),o("p",null,"Pán tvoj vie, že ostávaš hore o niečo menej ako dve tretiny noci, jej polovicu a jej tretinu a hore ostáva aj skupina z radov tých, ktorí sú s tebou. Boh stanovuje noc a deň. Vedel, že to nespočítate, preto prijal od vás pokánie. Prednášajte, koľko sa dá z Koránu. Vedel, že budú medzi vami chorí a ďalší budú cestovať po zemi, sledujúc Božie dobrodenie a ďalší budú bojovať za cestu, ktorú Boh určil. Prednášajte, koľko sa z neho dá. Konajte modlitbu a dávajte zakat a poskytnite Bohu dobrú pôžičku. Akékoľvek dobro, ktoré by ste vykonali pre svoje duše, nájdete u Boha, bude ešte lepšie a s ešte väčšou odmenou. Proste Boha o odpustenie. Veď Boh je odpúšťajúci a milostivý.")],-1),I=o("details",{class:"details custom-block"},[o("summary",null,"Tafsir"),o("p",null,"Boh vedel, že nedokážete vyrátať presne, kedy uplynú dve tretiny noci alebo polovica noci alebo jej tretina, preto prijal od vás pokánie za to a túto povinnosť vám zmiernil."),o("p",null,"Tento verš bol zoslaný potom, ako boli zoslané verše 2 až 4 tejto kapitoly. Muslimovia chceli spomenuté verše dodržať a modliť sa aj časť noci, ale nevedeli, kedy presne uplynie polovica noci alebo jej tretina. Preto ostávali hore modliť sa, kým nezačalo svitať, z obavy, aby sa nepomýlili. To ich ale vyčerpávalo, až im nohy opuchli a oni zbledli. Preto Boh zoslal tento verš, v ktorom im hovorí, že Boh vie, že nedokážu vypočítať, kedy presne nastane polovica alebo tretina noci a ponechal modlenie počas noci na individuálne možnosti modliacich sa.")],-1);function z(N,g,D,R,E,B){const a=t("Badge");return r(),d("div",null,[l,e(a,{type:"info",text:"73:1",class:"badge"}),n,e(a,{type:"info",text:"73:2",class:"badge"}),v,e(a,{type:"info",text:"73:3",class:"badge"}),c,e(a,{type:"info",text:"73:4",class:"badge"}),h,e(a,{type:"info",text:"73:5",class:"badge"}),p,e(a,{type:"info",text:"73:6",class:"badge"}),m,e(a,{type:"info",text:"73:7",class:"badge"}),u,e(a,{type:"info",text:"73:8",class:"badge"}),b,e(a,{type:"info",text:"73:9",class:"badge"}),T,e(a,{type:"info",text:"73:10",class:"badge"}),k,e(a,{type:"info",text:"73:11",class:"badge"}),P,e(a,{type:"info",text:"73:12",class:"badge"}),A,e(a,{type:"info",text:"73:13",class:"badge"}),y,e(a,{type:"info",text:"73:14",class:"badge"}),S,e(a,{type:"info",text:"73:15",class:"badge"}),f,e(a,{type:"info",text:"73:16",class:"badge"}),j,e(a,{type:"info",text:"73:17",class:"badge"}),V,e(a,{type:"info",text:"73:18",class:"badge"}),C,e(a,{type:"info",text:"73:19",class:"badge"}),q,e(a,{type:"info",text:"73:20",class:"badge"}),x,I])}const Q=i(_,[["render",z]]);export{K as __pageData,Q as default};
