import{_ as o,D as t,c as r,I as e,a2 as s,j as i,o as d}from"./chunks/framework.-9P5VKzf.js";const F=JSON.parse('{"title":"","description":"","frontmatter":{"outline":"deep"},"headers":[],"relativePath":"quran/81.md","filePath":"quran/81.md"}'),_={name:"quran/81.md"},l=s('<div class="chapter-title-wrapper"><div class="chapter-title">81</div><div class="chapter-title-slovak">Zvinutie</div><div class="chapter-opening">115</div><div class="chapter-opening-slovak">In the Name of Allah, the Most Compassionate, Most Merciful.</div></div><div class="intro2-wrapper"><div class="chapter-info-wrapper"><div class="chapter-info-translation">Translation</div><div class="chapter-info-name">Dr. Mustafa Khattab, The Clear Quran</div></div></div><h2 id="_1" tabindex="-1">1 <a class="header-anchor" href="#_1" aria-label="Permalink to &quot;1&quot;">​</a></h2>',3),v=s('<div><div class="main-verse"><h1 class="verse-arabic">إِذَا ٱلشَّمْسُ كُوِّرَتْ ١</h1></div><p>Až bude slnko zvinuté,</p></div><div class="break"></div><h2 id="_2" tabindex="-1">2 <a class="header-anchor" href="#_2" aria-label="Permalink to &quot;2&quot;">​</a></h2>',3),n=s('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَا ٱلنُّجُومُ ٱنكَدَرَتْ ٢</h1></div><p>A až sa hviezdy rozptýlia,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Slovo Inkadarat, ktoré sme preložili ako rozptýlenie, môže znamenať aj zrýchlenie, padnutie, zrútenie alebo stíchnutie svetla a jeho zblednutie.</p></details><div class="break"></div><h2 id="_3" tabindex="-1">3 <a class="header-anchor" href="#_3" aria-label="Permalink to &quot;3&quot;">​</a></h2>',4),c=s('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَا ٱلْجِبَالُ سُيِّرَتْ ٣</h1></div><p>A až sa hory do pohybu dajú,</p></div><div class="break"></div><h2 id="_4" tabindex="-1">4 <a class="header-anchor" href="#_4" aria-label="Permalink to &quot;4&quot;">​</a></h2>',3),h=s('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَا ٱلْعِشَارُ عُطِّلَتْ ٤</h1></div><p>A až ťarchavé ťavy budú zanedbané,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Podľa výkladov Koránu sa tu spomínajú ťavy, pretože ony boli najcennejším majetkom Arabov vo vtedajšej dobe. Tento verš však vzhľadom na slová, ktorými bol vyjadrený, môže niesť aj iný význam, a to: A až sa zastaví funkčnosť mračien (ktoré vodu nesú so sebou). Rozhodujúce pre výklad tohto verša je slovo Išár, ktoré sme preložili ako ťarchavé ťavy. Je to najzaužívanejší výklad. Toto slovo však môže znamenať aj mračná prinášajúce dážď. Môže však vyjadrovať aj číslicu desať. Jeden z autorov novodobých výkladov píše, že toto slovo môže poukazovať na rozdiel medzi slnečným a lunárnym kalendárom, kde je lunárny kalendár kratší o desať dní ako slnečný. To by potom, v spojitosti s predchádzajúcimi veršami znázorňovalo zastavenie sa rotácie zeme a zastavenie sa merania času.</p></details><div class="break"></div><h2 id="_5" tabindex="-1">5 <a class="header-anchor" href="#_5" aria-label="Permalink to &quot;5&quot;">​</a></h2>',4),p=s('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَا ٱلْوُحُوشُ حُشِرَتْ ٥</h1></div><p>A až divoké zvieratá budú zhromaždené,</p></div><div class="break"></div><h2 id="_6" tabindex="-1">6 <a class="header-anchor" href="#_6" aria-label="Permalink to &quot;6&quot;">​</a></h2>',3),m=s('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَا ٱلْبِحَارُ سُجِّرَتْ ٦</h1></div><p>A až sa moria zapália,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Slovo sužžirat, ktoré sme preložili ako zapália, môže však tiež znamenať: vyliať sa, prestať sa pohybovať a stíchnuť. Najviac sa však používa v zmysle zapáliť sa. Podstatou ostáva, že moria sa dostanú do iného, neprirodzeného stavu, než v akom ich poznáme.</p></details><div class="break"></div><h2 id="_7" tabindex="-1">7 <a class="header-anchor" href="#_7" aria-label="Permalink to &quot;7&quot;">​</a></h2>',4),b=s('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَا ٱلنُّفُوسُ زُوِّجَتْ ٧</h1></div><p>A až budú duše pridružené,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Tento verš môže niesť aj význam spojenia duší so svojím telom.</p></details><div class="break"></div><h2 id="_8" tabindex="-1">8 <a class="header-anchor" href="#_8" aria-label="Permalink to &quot;8&quot;">​</a></h2>',4),u=s('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَا ٱلْمَوْءُۥدَةُ سُئِلَتْ ٨</h1></div><p>A až zaživa pochované novorodeniatko bude opýtané,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Slovom Mawúda sa označovali novorodeniatka ženského pohlavia, ktoré bývali zaživa pochovávané do zeme v dobe pred príchodom islamu. Vtedy vládlo presvedčenie, že žena prináša len hanbu a poníženie nielen otcovi, ale aj celému rodu, preto narodenie dievčatka bolo jednou z najhanebnejších vecí u niektorých Arabov a aby sa vyhli hanbe, zakopávali novorodeniatka zaživa do zeme. Islam takýto čin zakázal a Boh v Koráne zoslal, že je to jeden z najväčších zločinov, ktoré vyvolávajú Jeho hnev.</p></details><div class="break"></div><h2 id="_9" tabindex="-1">9 <a class="header-anchor" href="#_9" aria-label="Permalink to &quot;9&quot;">​</a></h2>',4),T=s('<div><div class="main-verse"><h1 class="verse-arabic">بِأَىِّ ذَنۢبٍ قُتِلَتْ ٩</h1></div><p>Za aký hriech bolo zabité?</p></div><div class="break"></div><h2 id="_10" tabindex="-1">10 <a class="header-anchor" href="#_10" aria-label="Permalink to &quot;10&quot;">​</a></h2>',3),k=s('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَا ٱلصُّحُفُ نُشِرَتْ ١٠</h1></div><p>A až knihy budú roztvorené,</p></div><div class="break"></div><h2 id="_11" tabindex="-1">11 <a class="header-anchor" href="#_11" aria-label="Permalink to &quot;11&quot;">​</a></h2>',3),A=s('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَا ٱلسَّمَآءُ كُشِطَتْ ١١</h1></div><p>A až nebo bude odstránené,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Slovo Kušitat, ktoré sme vo verši preložili ako „odstránené“, v arabskom jazyku znamená odrať, vytrhnúť, odstrániť, dať preč. Toto slovo sa používa v súvislosti s odratím zvieraťa a znamená rýchle a prudké stiahnutie kože zo zvieraťa. V tomto prípade sa dá povedať, že bude stiahnutý zôkol našej zeme obal, ktorý ju ochraňuje.</p></details><div class="break"></div><h2 id="_12" tabindex="-1">12 <a class="header-anchor" href="#_12" aria-label="Permalink to &quot;12&quot;">​</a></h2>',4),P=s('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَا ٱلْجَحِيمُ سُعِّرَتْ ١٢</h1></div><p>A až peklo vzbĺkne,</p></div><div class="break"></div><h2 id="_13" tabindex="-1">13 <a class="header-anchor" href="#_13" aria-label="Permalink to &quot;13&quot;">​</a></h2>',3),z=s('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَا ٱلْجَنَّةُ أُزْلِفَتْ ١٣</h1></div><p>A až záhrada rajská bude priblížená,</p></div><div class="break"></div><h2 id="_14" tabindex="-1">14 <a class="header-anchor" href="#_14" aria-label="Permalink to &quot;14&quot;">​</a></h2>',3),S=s('<div><div class="main-verse"><h1 class="verse-arabic">عَلِمَتْ نَفْسٌ مَّآ أَحْضَرَتْ ١٤</h1></div><p>Bude poznať duša, čo si priniesla.</p></div><div class="break"></div><h2 id="_15" tabindex="-1">15 <a class="header-anchor" href="#_15" aria-label="Permalink to &quot;15&quot;">​</a></h2>',3),y=s('<div><div class="main-verse"><h1 class="verse-arabic">فَلَآ أُقْسِمُ بِٱلْخُنَّسِ ١٥</h1></div><p>Prisahám na Al Chunnas,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Slovo Al Chunnas, ktoré sme preložili ako hviezdy, môže mať aj užší význam, a to planéty, ktorých svetlo vidíme voľným okom v noci a ktoré slabne, až zmizne počas dňa.</p></details><div class="break"></div><h2 id="_16" tabindex="-1">16 <a class="header-anchor" href="#_16" aria-label="Permalink to &quot;16&quot;">​</a></h2>',4),f=s('<div><div class="main-verse"><h1 class="verse-arabic">ٱلْجَوَارِ ٱلْكُنَّسِ ١٦</h1></div><p>Bežiace, vracajúce sa na svoje stanovištia,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Ak by sme brali užší význam slova Al Chunnas v zmysle planét, potom by verš znel: Bežiace, vracajúce sa na svoje stanovištia (skrývajúce sa počas dňa).</p></details><div class="break"></div><h2 id="_17" tabindex="-1">17 <a class="header-anchor" href="#_17" aria-label="Permalink to &quot;17&quot;">​</a></h2>',4),j=s('<div><div class="main-verse"><h1 class="verse-arabic">وَٱلَّيْلِ إِذَا عَسْعَسَ ١٧</h1></div><p>A prisahám na noc, keď sa rozprestiera,</p></div><div class="break"></div><h2 id="_18" tabindex="-1">18 <a class="header-anchor" href="#_18" aria-label="Permalink to &quot;18&quot;">​</a></h2>',3),V=s('<div><div class="main-verse"><h1 class="verse-arabic">وَٱلصُّبْحِ إِذَا تَنَفَّسَ ١٨</h1></div><p>A prisahám na ráno, keď naberá dych,</p></div><div class="break"></div><h2 id="_19" tabindex="-1">19 <a class="header-anchor" href="#_19" aria-label="Permalink to &quot;19&quot;">​</a></h2>',3),C=s('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّهُۥ لَقَوْلُ رَسُولٍ كَرِيمٍ ١٩</h1></div><p>Že ho prináša posol vznešený,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Boh prisahal na všetky uvedené prírodné úkazy, ktoré stvoril, že Korán, ktorý máme v rukách, od Boha pochádza a že s ním k poslovi a prorokovi Muhammadovi prichádza anjel Gabriel, ktorý je opísaný ako vznešený posol.</p></details><div class="break"></div><h2 id="_20" tabindex="-1">20 <a class="header-anchor" href="#_20" aria-label="Permalink to &quot;20&quot;">​</a></h2>',4),I=s('<div><div class="main-verse"><h1 class="verse-arabic">ذِى قُوَّةٍ عِندَ ذِى ٱلْعَرْشِ مَكِينٍ ٢٠</h1></div><p>Obdarený silou. Má pevné postavenie u Toho, kto Trón má.</p></div><div class="break"></div><h2 id="_21" tabindex="-1">21 <a class="header-anchor" href="#_21" aria-label="Permalink to &quot;21&quot;">​</a></h2>',3),q=s('<div><div class="main-verse"><h1 class="verse-arabic">مُّطَاعٍ ثَمَّ أَمِينٍ ٢١</h1></div><p>Poslúchaný je. Presne odovzdá, čo mu je zverené.</p></div><div class="break"></div><h2 id="_22" tabindex="-1">22 <a class="header-anchor" href="#_22" aria-label="Permalink to &quot;22&quot;">​</a></h2>',3),x=s('<div><div class="main-verse"><h1 class="verse-arabic">وَمَا صَاحِبُكُم بِمَجْنُونٍ ٢٢</h1></div><p>Váš spoločník veru bláznom nie je,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>V prvých rokoch posolstva potom, ako Boh prikázal poslovi Muhammadovi, aby začal verejne vyzývať ľudí v Mekke k islamu, sa zvýšil počet tých, ktorým takáto výzva prekážala. Mnohí sa voči posolstvu a voči prorokovi Muhammadovi postavili aj napriek tomu, že verili, že slová Koránu majú nadprirodzený pôvod. Pýcha, povýšenosť, strach o stratu spoločenského postavenia, strach o stratu nečestných majetkov a iné bolo v skutočnosti to, čo ich viedlo k tomu, že sa voči posolstvu postavili. Ich odmietavý postoj sa prejavoval v rôznych podobách, počínajúc verbálnymi útokmi a končiac použitím sily so snahou proroka zabiť. Jednou z vecí, ktoré vymyslela skupina ľudí, ktorá sa proti posolstvu postavila, bolo, že opísala posla Muhammada ako blázna, ktorý blúzni. Takéto tvrdenie však dlho neobstálo, pretože keď ľudia slová Koránu počuli, sami videli, že je to niečo, čo sa blúzneniu v žiadnom prípade nepodobá. Boh aj napriek tomu ľuďom potvrdzuje, že to, čo posol Muhammad videl, nebolo blúznenie, ale bol to v skutočnosti anjel Gabriel.</p></details><div class="break"></div><h2 id="_23" tabindex="-1">23 <a class="header-anchor" href="#_23" aria-label="Permalink to &quot;23&quot;">​</a></h2>',4),N=s('<div><div class="main-verse"><h1 class="verse-arabic">وَلَقَدْ رَءَاهُ بِٱلْأُفُقِ ٱلْمُبِينِ ٢٣</h1></div><p>On ho veru videl na obzore jasnom,</p></div><div class="break"></div><h2 id="_24" tabindex="-1">24 <a class="header-anchor" href="#_24" aria-label="Permalink to &quot;24&quot;">​</a></h2>',3),g=s('<div><div class="main-verse"><h1 class="verse-arabic">وَمَا هُوَ عَلَى ٱلْغَيْبِ بِضَنِينٍ ٢٤</h1></div><p>On nie je na to, čo nie je známe, skúpy,</p></div><div class="break"></div><h2 id="_25" tabindex="-1">25 <a class="header-anchor" href="#_25" aria-label="Permalink to &quot;25&quot;">​</a></h2>',3),D=s('<div><div class="main-verse"><h1 class="verse-arabic">وَمَا هُوَ بِقَوْلِ شَيْطَـٰنٍ رَّجِيمٍ ٢٥</h1></div><p>Nie je to slovo satana zavrhnutého,</p></div><div class="break"></div><h2 id="_26" tabindex="-1">26 <a class="header-anchor" href="#_26" aria-label="Permalink to &quot;26&quot;">​</a></h2>',3),E=s('<div><div class="main-verse"><h1 class="verse-arabic">فَأَيْنَ تَذْهَبُونَ ٢٦</h1></div><p>Kam teda chcete ísť?</p></div><div class="break"></div><h2 id="_27" tabindex="-1">27 <a class="header-anchor" href="#_27" aria-label="Permalink to &quot;27&quot;">​</a></h2>',3),R=s('<div><div class="main-verse"><h1 class="verse-arabic">إِنْ هُوَ إِلَّا ذِكْرٌ لِّلْعَـٰلَمِينَ ٢٧</h1></div><p>Je to len pripomenutie pre stvorených,</p></div><div class="break"></div><h2 id="_28" tabindex="-1">28 <a class="header-anchor" href="#_28" aria-label="Permalink to &quot;28&quot;">​</a></h2>',3),M=s('<div><div class="main-verse"><h1 class="verse-arabic">لِمَن شَآءَ مِنكُمْ أَن يَسْتَقِيمَ ٢٨</h1></div><p>Pre tých z vás, ktorí by sa chceli napraviť.</p></div><div class="break"></div><h2 id="_29" tabindex="-1">29 <a class="header-anchor" href="#_29" aria-label="Permalink to &quot;29&quot;">​</a></h2>',3),B=i("div",null,[i("div",{class:"main-verse"},[i("h1",{class:"verse-arabic"},"وَمَا تَشَآءُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُ رَبُّ ٱلْعَـٰلَمِينَ ٢٩")]),i("p",null,"Nebudete môcť chcieť bez toho, aby to Pán tvorstva chcel.")],-1);function K(J,w,O,$,G,Z){const a=t("Badge");return d(),r("div",null,[l,e(a,{type:"info",text:"81:1",class:"badge"}),v,e(a,{type:"info",text:"81:2",class:"badge"}),n,e(a,{type:"info",text:"81:3",class:"badge"}),c,e(a,{type:"info",text:"81:4",class:"badge"}),h,e(a,{type:"info",text:"81:5",class:"badge"}),p,e(a,{type:"info",text:"81:6",class:"badge"}),m,e(a,{type:"info",text:"81:7",class:"badge"}),b,e(a,{type:"info",text:"81:8",class:"badge"}),u,e(a,{type:"info",text:"81:9",class:"badge"}),T,e(a,{type:"info",text:"81:10",class:"badge"}),k,e(a,{type:"info",text:"81:11",class:"badge"}),A,e(a,{type:"info",text:"81:12",class:"badge"}),P,e(a,{type:"info",text:"81:13",class:"badge"}),z,e(a,{type:"info",text:"81:14",class:"badge"}),S,e(a,{type:"info",text:"81:15",class:"badge"}),y,e(a,{type:"info",text:"81:16",class:"badge"}),f,e(a,{type:"info",text:"81:17",class:"badge"}),j,e(a,{type:"info",text:"81:18",class:"badge"}),V,e(a,{type:"info",text:"81:19",class:"badge"}),C,e(a,{type:"info",text:"81:20",class:"badge"}),I,e(a,{type:"info",text:"81:21",class:"badge"}),q,e(a,{type:"info",text:"81:22",class:"badge"}),x,e(a,{type:"info",text:"81:23",class:"badge"}),N,e(a,{type:"info",text:"81:24",class:"badge"}),g,e(a,{type:"info",text:"81:25",class:"badge"}),D,e(a,{type:"info",text:"81:26",class:"badge"}),E,e(a,{type:"info",text:"81:27",class:"badge"}),R,e(a,{type:"info",text:"81:28",class:"badge"}),M,e(a,{type:"info",text:"81:29",class:"badge"}),B])}const H=o(_,[["render",K]]);export{F as __pageData,H as default};
