import{_ as s,D as t,c as r,I as e,a2 as o,j as i,o as n}from"./chunks/framework.-9P5VKzf.js";const q=JSON.parse('{"title":"Quran","titleTemplate":false,"description":"","frontmatter":{"outline":"deep","title":"Quran","titleTemplate":false},"headers":[],"relativePath":"quran/94.md","filePath":"quran/94.md"}'),d={name:"quran/94.md"},l=o('<div class="chapter-title-wrapper"><div class="chapter-title">94</div><div class="chapter-title-slovak">Uvoľnenie</div><div class="chapter-opening">115</div><div class="chapter-opening-slovak">In the Name of Allah, the Most Compassionate, Most Merciful.</div></div><div class="intro2-wrapper"><div class="chapter-info-wrapper"><div class="chapter-info-translation">Translation</div><div class="chapter-info-name">Dr. Mustafa Khattab, The Clear Quran</div></div></div><h2 id="_1" tabindex="-1">1 <a class="header-anchor" href="#_1" aria-label="Permalink to &quot;1&quot;">​</a></h2>',3),v=o('<div><div class="main-verse"><h1 class="verse-arabic">أَلَمْ نَشْرَحْ لَكَ صَدْرَكَ ١</h1></div><p>A či sme ti neuvoľnili hruď tvoju?</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Zvestovanie a vnukanie Koránu poslovi a prorokovi Muhammadovi (p.) prostredníctvom anjela Gabriela bolo pre posla Muhammada veľmi náročné a vyčerpávajúce. Veď sám Boh, nad ktorým vyššieho niet, v Koráne hovorí: „Keby sme zoslali dole tento Korán na horu, uvidel by si ju pokornú, popraskanú od strachu pred Bohom“ verš 59:21.</p></details><div class="break"></div><h2 id="_2" tabindex="-1">2 <a class="header-anchor" href="#_2" aria-label="Permalink to &quot;2&quot;">​</a></h2>',4),_=o('<div><div class="main-verse"><h1 class="verse-arabic">وَوَضَعْنَا عَنكَ وِزْرَكَ ٢</h1></div><p>A sňali sme z teba tvoje bremeno,</p></div><div class="break"></div><h2 id="_3" tabindex="-1">3 <a class="header-anchor" href="#_3" aria-label="Permalink to &quot;3&quot;">​</a></h2>',3),c=o('<div><div class="main-verse"><h1 class="verse-arabic">ٱلَّذِىٓ أَنقَضَ ظَهْرَكَ ٣</h1></div><p>Ktoré ťažilo tvoj chrbát,</p></div><div class="break"></div><h2 id="_4" tabindex="-1">4 <a class="header-anchor" href="#_4" aria-label="Permalink to &quot;4&quot;">​</a></h2>',3),h=o('<div><div class="main-verse"><h1 class="verse-arabic">وَرَفَعْنَا لَكَ ذِكْرَكَ ٤</h1></div><p>A pozdvihli sme postavenie tvoje.</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Jeden z výrokov proroka Muhammada (p.) hovorí: Prišiel ku mne anjel Gabriel a povedal mi: „Muhammad, tvoj Pán hovorí: Vieš, ako som pozdvihol vysoko tvoje postavenie?“ Povedal som: „Boh, nad ktorým niet vyššieho, vie.“ (Anjel Gabriel) Povedal: „Keď Som (Boh) spomenutý, si spomenutý so mnou“. To sa nám javí napríklad pri volaní k modlitbe, ktoré znie: „Svedčím, že niet boha okrem Boha a svedčím, že Muhammad je Božím poslom ....“, alebo v samotnej modlitbe a pod. Tým sa vlastne postavenie a spomienka na proroka dostala tak vysoko, že keď je spomenutý Boh, je tesne za ním spomenutý aj Muhammad ako posol Boží.</p><p>Táto kapitola bola zoslaná poslovi a prorokovi Muhammadovi (p.) v prvých rokoch islamu, keď bol ešte v Mekke (pred odchodom do Mediny) a v čase, keď boli muslimovia vo veľkej tiesni a keď voči nim ich nepriatelia zostrili boj a nenávisť. Boh pripomína prorokovi (p.), čo všetko mu dal a že jemu i veriacim čoskoro pomôže, len musia byť trpezliví a mať výdrž.</p></details><div class="break"></div><h2 id="_5" tabindex="-1">5 <a class="header-anchor" href="#_5" aria-label="Permalink to &quot;5&quot;">​</a></h2>',4),p=o('<div><div class="main-verse"><h1 class="verse-arabic">فَإِنَّ مَعَ ٱلْعُسْرِ يُسْرًا ٥</h1></div><p>S ťažkosťou prichádza aj uľahčenie,</p></div><div class="break"></div><h2 id="_6" tabindex="-1">6 <a class="header-anchor" href="#_6" aria-label="Permalink to &quot;6&quot;">​</a></h2>',3),m=o('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ مَعَ ٱلْعُسْرِ يُسْرًا ٦</h1></div><p>S ťažkosťou prichádza aj uľahčenie.</p></div><div class="break"></div><h2 id="_7" tabindex="-1">7 <a class="header-anchor" href="#_7" aria-label="Permalink to &quot;7&quot;">​</a></h2>',3),u=o('<div><div class="main-verse"><h1 class="verse-arabic">فَإِذَا فَرَغْتَ فَٱنصَبْ ٧</h1></div><p>Keď teda skončíš, tak sa vzpriam,</p></div><div class="break"></div><h2 id="_8" tabindex="-1">8 <a class="header-anchor" href="#_8" aria-label="Permalink to &quot;8&quot;">​</a></h2>',3),k=i("div",null,[i("div",{class:"main-verse"},[i("h1",{class:"verse-arabic"},"وَإِلَىٰ رَبِّكَ فَٱرْغَب ٨")]),i("p",null,"A k Pánovi svojmu spieť chci.")],-1),b=i("details",{class:"details custom-block"},[i("summary",null,"Tafsir"),i("p",null,"Význam predchádzajúcich troch veršov adresovaných prorokovi Muhammadovi (p.) spočíva v tom, že proroka nabádajú, aby po skončení svojich každodenných povinností a problémov uvoľnil svoju myseľ, vzpriamil sa a pripravil sa k modlitbe a uctievaniu Pána svojho. Uvedené verše sú síce adresované prorokovi Muhammadovi (p.), avšak ukazujú ostatným veriacim, že by nemali zabúdať na modlenie a na spomínanie si na Boha, keď skončia svoje každodenné povinnosti a mali by si časť svojho dňa vyhradiť na tento účel.")],-1);function T(f,j,P,A,S,y){const a=t("Badge");return n(),r("div",null,[l,e(a,{type:"info",text:"94:1",class:"badge"}),v,e(a,{type:"info",text:"94:2",class:"badge"}),_,e(a,{type:"info",text:"94:3",class:"badge"}),c,e(a,{type:"info",text:"94:4",class:"badge"}),h,e(a,{type:"info",text:"94:5",class:"badge"}),p,e(a,{type:"info",text:"94:6",class:"badge"}),m,e(a,{type:"info",text:"94:7",class:"badge"}),u,e(a,{type:"info",text:"94:8",class:"badge"}),k,b])}const x=s(d,[["render",T]]);export{q as __pageData,x as default};
