import{_ as i,D as t,c as r,I as o,a2 as s,j as a,o as l}from"./chunks/framework.-9P5VKzf.js";const f=JSON.parse(`{"title":"Kur'an","titleTemplate":false,"description":"","frontmatter":{"outline":"deep","title":"Kur'an","titleTemplate":false},"headers":[],"relativePath":"tr/quran/105.md","filePath":"tr/quran/105.md"}`),n={name:"tr/quran/105.md"},d=s('<div class="chapter-title-wrapper"><div class="chapter-title">105</div><div class="chapter-title-slovak">Slon</div><div class="chapter-opening">115</div><div class="chapter-opening-slovak">Rahman ve Rahim olan Allah&#39;ın adıyla</div></div><div class="intro2-wrapper"><div class="chapter-info-wrapper"><div class="chapter-info-translation">Meali</div><div class="chapter-info-name">Diyanet Vakfı Meali</div></div></div><h2 id="_1" tabindex="-1">1 <a class="header-anchor" href="#_1" aria-label="Permalink to &quot;1&quot;">​</a></h2>',3),v=s('<div><div class="main-verse"><h1 class="verse-arabic">أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِأَصْحَـٰبِ ٱلْفِيلِ ١</h1></div><p>A či si nevidel, čo Pán tvoj urobil s Ľuďmi slona?</p></div><details class="details custom-block"><summary>Tafsir</summary><p>O príbehu Ľudí slona (arabsky Asháb Al Fíľ) islamskí učenci napísali: Tradovalo sa, že Abraha Al Ašram, kráľ Jemenu a Etiópie, postavil v Sane veľký kostol a chcel, aby k nemu pútnici chodili namiesto ku Kábe v Mekke (príbeh sa odohrával ešte pred posolstvom posla a proroka Muhammada). Tento úmysel sa kráľovi nevydaril, čo ho veľmi rozhnevalo a prisahal, že Kábu zbúra. Vystrojil obrovské vojsko spolu so slonmi, na čele ktorých bol jeden obrovský slon. Keď sa vojsko priblížilo k Mekke, jej obyvatelia utiekli do hôr zo strachu pred ním. Predtým než opustili Mekku, sa na Kábu naposledy pozreli a povedali: „Dom, t.j. Kába, má Pána, ktorý ho ochráni“. Keď už vojsko bolo blízko Mekky, Boh najmocnejší zoslal na vojsko Abrahu čierne vtáky. Každý vták mal tri ohnivé kamene, jeden v zobáku a po jednom v každej nohe. Vtáky vrhli svoje kamene na vojsko a vyhubili ho úplne. Tak sa stali Ľudia slona ponaučením pre ľudí. Aj z tohto dôvodu si všetky ostatné kmene i národy vážili Qurejšovcov (rod posla a proroka Muhammada). Tento príbeh sa odohral v tom istom roku, keď sa posol a prorok Muhammad narodil (rok 570 podľa gregoriánskeho letopočtu).</p></details><div class="break"></div><h2 id="_2" tabindex="-1">2 <a class="header-anchor" href="#_2" aria-label="Permalink to &quot;2&quot;">​</a></h2>',4),c=s('<div><div class="main-verse"><h1 class="verse-arabic">أَلَمْ يَجْعَلْ كَيْدَهُمْ فِى تَضْلِيلٍ ٢</h1></div><p>A či neučinil, aby ich úklady do bludu speli (kuli svoje úklady, ale Boh ich obrátil proti nim samým, a tak skĺzli zo správnej cesty),</p></div><div class="break"></div><h2 id="_3" tabindex="-1">3 <a class="header-anchor" href="#_3" aria-label="Permalink to &quot;3&quot;">​</a></h2>',3),h=s('<div><div class="main-verse"><h1 class="verse-arabic">وَأَرْسَلَ عَلَيْهِمْ طَيْرًا أَبَابِيلَ ٣</h1></div><p>A poslal na nich kŕdle vtákov (jeden po druhom),</p></div><div class="break"></div><h2 id="_4" tabindex="-1">4 <a class="header-anchor" href="#_4" aria-label="Permalink to &quot;4&quot;">​</a></h2>',3),p=s('<div><div class="main-verse"><h1 class="verse-arabic">تَرْمِيهِم بِحِجَارَةٍ مِّن سِجِّيلٍ ٤</h1></div><p>Ktoré na nich vrhali kamene z hliny poskladanej (pozri vysvetlivku k veršu 11:82)</p></div><div class="break"></div><h2 id="_5" tabindex="-1">5 <a class="header-anchor" href="#_5" aria-label="Permalink to &quot;5&quot;">​</a></h2>',3),k=a("div",null,[a("div",{class:"main-verse"},[a("h1",{class:"verse-arabic"},"فَجَعَلَهُمْ كَعَصْفٍ مَّأْكُولٍ ٥")]),a("p",null,"A tak (Boh) ich (armádu) učinil ako vyžrané strnisko (rozdrvenú, skosenú a bezvládnu).")],-1),m=a("details",{class:"details custom-block"},[a("summary",null,"Tafsir"),a("p",null,"Skutočnosťou ostáva, že po tejto udalosti sa stal kmeň Qurejšovcov, ku ktorému patril aj rod Hášimovcov, rod, z ktorého pochádzal otec proroka Muhammada, obávaným a rešpektovaným kmeňom. Svedkovia rozniesli po Arabskom polostrove svedectvá o tom, ako celá armáda so slonmi dopadla. Po tejto udalosti Qurejšovci zišli z hôr, zo svojich úkrytov a podelili sa o veľkú korisť, ktorá ostala na mieste vyhubenej armády. Ešte dôležitejšie než samotná veľká korisť, ktorú Qurejšovci bez boja získali, bola váženosť medzi ostatnými kmeňmi. Každý, koho by predtým napadlo napadnúť Mekku, už takúto predstavu zavrhol a to aj napriek tomu, že Qurejšovci už teraz oplývali ešte väčším bohatstvom než predtým. Mnohí sa už skôr snažili získať si ich na svoju stranu. Táto udalosť, ako už bolo spomenuté, zabezpečila pre Qurejšovcov a ich obchodné karavány bezpečný prechod cez mnohé územia, kde na nich číhalo nebezpečenstvo prepadu a olúpenia."),a("p",null,"A práve o tomto dare, o týchto obchodných cestách hovorí nasledujúca kapitola zvaná Qurejš.")],-1);function _(u,b,j,T,y,z){const e=t("Badge");return l(),r("div",null,[d,o(e,{type:"info",text:"105:1",class:"badge"}),v,o(e,{type:"info",text:"105:2",class:"badge"}),c,o(e,{type:"info",text:"105:3",class:"badge"}),h,o(e,{type:"info",text:"105:4",class:"badge"}),p,o(e,{type:"info",text:"105:5",class:"badge"}),k,m])}const P=i(n,[["render",_]]);export{f as __pageData,P as default};
