import{_ as s,D as _,c as t,I as e,a2 as i,j as o,o as d}from"./chunks/framework.-9P5VKzf.js";const F=JSON.parse(`{"title":"Kur'an","titleTemplate":false,"description":"","frontmatter":{"outline":"deep","title":"Kur'an","titleTemplate":false},"headers":[],"relativePath":"tr/quran/71.md","filePath":"tr/quran/71.md"}`),v={name:"tr/quran/71.md"},r=i('<div class="chapter-title-wrapper"><div class="chapter-title">71</div><div class="chapter-title-slovak">Noe</div><div class="chapter-opening">115</div><div class="chapter-opening-slovak">Rahman ve Rahim olan Allah&#39;ın adıyla</div></div><div class="intro2-wrapper"><div class="chapter-info-wrapper"><div class="chapter-info-translation">Meali</div><div class="chapter-info-name">Diyanet Vakfı Meali</div></div></div><h2 id="_1" tabindex="-1">1 <a class="header-anchor" href="#_1" aria-label="Permalink to &quot;1&quot;">​</a></h2>',3),l=i('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّآ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِۦٓ أَنْ أَنذِرْ قَوْمَكَ مِن قَبْلِ أَن يَأْتِيَهُمْ عَذَابٌ أَلِيمٌ ١</h1></div><p>My sme poslali Núha (Noema) k ľuďom jeho (a poverili sme ho): „Varuj svojich ľudí, predtým než k nim príde trápenie bolestivé“.</p></div><div class="break"></div><h2 id="_2" tabindex="-1">2 <a class="header-anchor" href="#_2" aria-label="Permalink to &quot;2&quot;">​</a></h2>',3),n=i('<div><div class="main-verse"><h1 class="verse-arabic">قَالَ يَـٰقَوْمِ إِنِّى لَكُمْ نَذِيرٌ مُّبِينٌ ٢</h1></div><p>Povedal (Noe svojim ľuďom): „Ľudia moji! Ja som určený pre vás ako varovateľ zjavný (o ktorom niet pochýb a mojou úlohou je vás vyzvať)“,</p></div><div class="break"></div><h2 id="_3" tabindex="-1">3 <a class="header-anchor" href="#_3" aria-label="Permalink to &quot;3&quot;">​</a></h2>',3),h=i('<div><div class="main-verse"><h1 class="verse-arabic">أَنِ ٱعْبُدُوا ٱللَّهَ وَٱتَّقُوهُ وَأَطِيعُونِ ٣</h1></div><p>„Uctievajte Boha, bojte sa Ho a poslúchnite ma“,</p></div><div class="break"></div><h2 id="_4" tabindex="-1">4 <a class="header-anchor" href="#_4" aria-label="Permalink to &quot;4&quot;">​</a></h2>',3),c=i('<div><div class="main-verse"><h1 class="verse-arabic">يَغْفِرْ لَكُم مِّن ذُنُوبِكُمْ وَيُؤَخِّرْكُمْ إِلَىٰٓ أَجَلٍ مُّسَمًّى ۚ إِنَّ أَجَلَ ٱللَّهِ إِذَا جَآءَ لَا يُؤَخَّرُ ۖ لَوْ كُنتُمْ تَعْلَمُونَ ٤</h1></div><p>„Odpustí vám (Boh) z vašich hriechov a odloží vás (váš odchod zo života pozemského) na čas stanovený (nevezme vás z tohto života predčasne). Čas Bohom určený (na odchod človeka zo života pozemského), keď príde, odložený nebude (váš odchod zo života pozemského nik bez Božieho príkazu neodloží). Keby ste to len vedeli (a uvedomovali si túto skutočnosť, neboli by ste vieru a varovania odmietali)“.</p></div><div class="break"></div><h2 id="_5" tabindex="-1">5 <a class="header-anchor" href="#_5" aria-label="Permalink to &quot;5&quot;">​</a></h2>',3),m=i('<div><div class="main-verse"><h1 class="verse-arabic">قَالَ رَبِّ إِنِّى دَعَوْتُ قَوْمِى لَيْلًا وَنَهَارًا ٥</h1></div><p>Povedal (Noe potom, ako to všetko svojim ľuďom oznámil): „Pane môj! Ja som vyzýval svojich ľudí v noci i vo dne“,</p></div><div class="break"></div><h2 id="_6" tabindex="-1">6 <a class="header-anchor" href="#_6" aria-label="Permalink to &quot;6&quot;">​</a></h2>',3),b=i('<div><div class="main-verse"><h1 class="verse-arabic">فَلَمْ يَزِدْهُمْ دُعَآءِىٓ إِلَّا فِرَارًا ٦</h1></div><p>„Moja výzva im však nepridala nič iné než útek (pred pravdou, ku ktorej som ich vyzýval)“,</p></div><div class="break"></div><h2 id="_7" tabindex="-1">7 <a class="header-anchor" href="#_7" aria-label="Permalink to &quot;7&quot;">​</a></h2>',3),p=i('<div><div class="main-verse"><h1 class="verse-arabic">وَإِنِّى كُلَّمَا دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُوٓا أَصَـٰبِعَهُمْ فِىٓ ءَاذَانِهِمْ وَٱسْتَغْشَوْا ثِيَابَهُمْ وَأَصَرُّوا وَٱسْتَكْبَرُوا ٱسْتِكْبَارًا ٧</h1></div><p>„Vždy, keď som ich vyzval (k viere v Boha a k nasledovaniu toho, čo si uložil), aby si im odpustil (ich hriechy), vložili svoje prsty do svojich uší a zakryli sa svojimi šatami a vedome trvali na tom, na čom sú (na odmietaní viery) a povyšovali sa (nad to, čo som im hovoril) povyšovaním (veľkým)“,</p></div><div class="break"></div><h2 id="_8" tabindex="-1">8 <a class="header-anchor" href="#_8" aria-label="Permalink to &quot;8&quot;">​</a></h2>',3),T=i('<div><div class="main-verse"><h1 class="verse-arabic">ثُمَّ إِنِّى دَعَوْتُهُمْ جِهَارًا ٨</h1></div><p>„Potom som ich ja vyzval (k viere) verejne“,</p></div><div class="break"></div><h2 id="_9" tabindex="-1">9 <a class="header-anchor" href="#_9" aria-label="Permalink to &quot;9&quot;">​</a></h2>',3),u=i('<div><div class="main-verse"><h1 class="verse-arabic">ثُمَّ إِنِّىٓ أَعْلَنتُ لَهُمْ وَأَسْرَرْتُ لَهُمْ إِسْرَارًا ٩</h1></div><p>„Potom som im to (posolstvo) oznámil verejne i potajomky (som ich s tým oboznámil) skryto (ďaleko od očí ostatných, aby sa nebáli a neostýchali)“,</p></div><div class="break"></div><h2 id="_10" tabindex="-1">10 <a class="header-anchor" href="#_10" aria-label="Permalink to &quot;10&quot;">​</a></h2>',3),k=i('<div><div class="main-verse"><h1 class="verse-arabic">فَقُلْتُ ٱسْتَغْفِرُوا رَبَّكُمْ إِنَّهُۥ كَانَ غَفَّارًا ١٠</h1></div><p>„Povedal som (im): Proste o odpustenie Pána svojho. On je odpúšťajúci“,</p></div><div class="break"></div><h2 id="_11" tabindex="-1">11 <a class="header-anchor" href="#_11" aria-label="Permalink to &quot;11&quot;">​</a></h2>',3),P=i('<div><div class="main-verse"><h1 class="verse-arabic">يُرْسِلِ ٱلسَّمَآءَ عَلَيْكُم مِّدْرَارًا ١١</h1></div><p>„Pošle na vás mraky z neba s hojným dažďom“,</p></div><div class="break"></div><h2 id="_12" tabindex="-1">12 <a class="header-anchor" href="#_12" aria-label="Permalink to &quot;12&quot;">​</a></h2>',3),A=i('<div><div class="main-verse"><h1 class="verse-arabic">وَيُمْدِدْكُم بِأَمْوَٰلٍ وَبَنِينَ وَيَجْعَل لَّكُمْ جَنَّـٰتٍ وَيَجْعَل لَّكُمْ أَنْهَـٰرًا ١٢</h1></div><p>„A posilní vás majetkami a synmi a učiní vám záhrady a učiní vám rieky“.</p></div><div class="break"></div><h2 id="_13" tabindex="-1">13 <a class="header-anchor" href="#_13" aria-label="Permalink to &quot;13&quot;">​</a></h2>',3),S=i('<div><div class="main-verse"><h1 class="verse-arabic">مَّا لَكُمْ لَا تَرْجُونَ لِلَّهِ وَقَارًا ١٣</h1></div><p>Čo je vám, že Bohu poctu nevzdávate (ako jednému jedinému Stvoriteľovi, ktorý má moc nad všetkým)?</p></div><div class="break"></div><h2 id="_14" tabindex="-1">14 <a class="header-anchor" href="#_14" aria-label="Permalink to &quot;14&quot;">​</a></h2>',3),y=i('<div><div class="main-verse"><h1 class="verse-arabic">وَقَدْ خَلَقَكُمْ أَطْوَارًا ١٤</h1></div><p>On (Boh) vás pritom stvoril po etapách (fázach, stupňoch),</p></div><div class="break"></div><h2 id="_15" tabindex="-1">15 <a class="header-anchor" href="#_15" aria-label="Permalink to &quot;15&quot;">​</a></h2>',3),f=i('<div><div class="main-verse"><h1 class="verse-arabic">أَلَمْ تَرَوْا كَيْفَ خَلَقَ ٱللَّهُ سَبْعَ سَمَـٰوَٰتٍ طِبَاقًا ١٥</h1></div><p>Nevideli ste, ako Boh stvoril sedem nebies úplne podobných?</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Výraz „úplne podobných“ môže niesť aj význam „jedno nebo nad druhým“ alebo „v určitých vrstvách“.</p></details><div class="break"></div><h2 id="_16" tabindex="-1">16 <a class="header-anchor" href="#_16" aria-label="Permalink to &quot;16&quot;">​</a></h2>',4),V=i('<div><div class="main-verse"><h1 class="verse-arabic">وَجَعَلَ ٱلْقَمَرَ فِيهِنَّ نُورًا وَجَعَلَ ٱلشَّمْسَ سِرَاجًا ١٦</h1></div><p>A učinil (Boh) v nich (v nebesiach) mesiac svetlom a učinil slnko lampou,</p></div><div class="break"></div><h2 id="_17" tabindex="-1">17 <a class="header-anchor" href="#_17" aria-label="Permalink to &quot;17&quot;">​</a></h2>',3),j=i('<div><div class="main-verse"><h1 class="verse-arabic">وَٱللَّهُ أَنۢبَتَكُم مِّنَ ٱلْأَرْضِ نَبَاتًا ١٧</h1></div><p>Boh vám dal vyrásť zo zeme vyrastaním,</p></div><div class="break"></div><h2 id="_18" tabindex="-1">18 <a class="header-anchor" href="#_18" aria-label="Permalink to &quot;18&quot;">​</a></h2>',3),q=i('<div><div class="main-verse"><h1 class="verse-arabic">ثُمَّ يُعِيدُكُمْ فِيهَا وَيُخْرِجُكُمْ إِخْرَاجًا ١٨</h1></div><p>Potom vás do nej vráti (po vašej smrti v živote pozemskom) a (potom) dá vám vyjsť von (z nej v deň zmŕtvychvstania) vyjdením (určitým a nepochybným, nik sa tomuto procesu nevyhne),</p></div><div class="break"></div><h2 id="_19" tabindex="-1">19 <a class="header-anchor" href="#_19" aria-label="Permalink to &quot;19&quot;">​</a></h2>',3),x=i('<div><div class="main-verse"><h1 class="verse-arabic">وَٱللَّهُ جَعَلَ لَكُمُ ٱلْأَرْضَ بِسَاطًا ١٩</h1></div><p>Boh vám (pre vás) učinil zem rozprestretú (širokú, vystretú ako koberec),</p></div><div class="break"></div><h2 id="_20" tabindex="-1">20 <a class="header-anchor" href="#_20" aria-label="Permalink to &quot;20&quot;">​</a></h2>',3),C=i('<div><div class="main-verse"><h1 class="verse-arabic">لِّتَسْلُكُوا مِنْهَا سُبُلًا فِجَاجًا ٢٠</h1></div><p>Aby ste na nej razili cesty široké.</p></div><div class="break"></div><h2 id="_21" tabindex="-1">21 <a class="header-anchor" href="#_21" aria-label="Permalink to &quot;21&quot;">​</a></h2>',3),I=i('<div><div class="main-verse"><h1 class="verse-arabic">قَالَ نُوحٌ رَّبِّ إِنَّهُمْ عَصَوْنِى وَٱتَّبَعُوا مَن لَّمْ يَزِدْهُ مَالُهُۥ وَوَلَدُهُۥٓ إِلَّا خَسَارًا ٢١</h1></div><p>Noe povedal: „Pane môj! Oni ma odmietli poslúchnuť a nasledovali toho (bohatých ľudí, ktorí vtedy tam žili), komu jeho majetky a deti nepridali nič iné, len stratu (z týchto majetkov mali jediný úžitok, že sa ešte viac hriechov dopúšťali)“,</p></div><div class="break"></div><h2 id="_22" tabindex="-1">22 <a class="header-anchor" href="#_22" aria-label="Permalink to &quot;22&quot;">​</a></h2>',3),z=i('<div><div class="main-verse"><h1 class="verse-arabic">وَمَكَرُوا مَكْرًا كُبَّارًا ٢٢</h1></div><p>„Kuli (tí bohatí a mocní z radov Noemových ľudí) úklady ohromné (aby ľudí od Noema odohnali)“,</p></div><div class="break"></div><h2 id="_23" tabindex="-1">23 <a class="header-anchor" href="#_23" aria-label="Permalink to &quot;23&quot;">​</a></h2>',3),N=i('<div><div class="main-verse"><h1 class="verse-arabic">وَقَالُوا لَا تَذَرُنَّ ءَالِهَتَكُمْ وَلَا تَذَرُنَّ وَدًّا وَلَا سُوَاعًا وَلَا يَغُوثَ وَيَعُوقَ وَنَسْرًا ٢٣</h1></div><p>„Povedali (títo bohatí a mocní): „Nezanechávajte (neopúšťajte) svoje božstvá a nezanechávajte Vadda, ani Suwáa, ani Jaghusa, Jaúqa a Nasra (boli to mená piatich najvýznamnejších modiel u nich)““,</p></div><div class="break"></div><h2 id="_24" tabindex="-1">24 <a class="header-anchor" href="#_24" aria-label="Permalink to &quot;24&quot;">​</a></h2>',3),g=i('<div><div class="main-verse"><h1 class="verse-arabic">وَقَدْ أَضَلُّوا كَثِيرًا ۖ وَلَا تَزِدِ ٱلظَّـٰلِمِينَ إِلَّا ضَلَـٰلًا ٢٤</h1></div><p>„Do bludu pritom uviedli (títo bohatí a mocní) veľa ľudí. Krivdiacim (Bože) nepridaj nič iné než blud (ešte väčší, než v akom boli, aby svoj blud, ktorý opustiť nechcú, ešte prehĺbili)“.</p></div><div class="break"></div><h2 id="_25" tabindex="-1">25 <a class="header-anchor" href="#_25" aria-label="Permalink to &quot;25&quot;">​</a></h2>',3),D=i('<div><div class="main-verse"><h1 class="verse-arabic">مِّمَّا خَطِيٓـَٔـٰتِهِمْ أُغْرِقُوا فَأُدْخِلُوا نَارًا فَلَمْ يَجِدُوا لَهُم مِّن دُونِ ٱللَّهِ أَنصَارًا ٢٥</h1></div><p>Pre svoje vedomé omyly (ktoré vedome robili) boli potopení (Noemovi ľudia) a boli uvedení do ohňa. A sami pre seba nenašli mimo Boha nikoho, kto by im pomohol a podporil ich. (Mimo Boha niet toho, kto by im pomohol, takže keď Boha odmietli, už nebolo toho, kto by im pomocnú ruku podal.)</p></div><div class="break"></div><h2 id="_26" tabindex="-1">26 <a class="header-anchor" href="#_26" aria-label="Permalink to &quot;26&quot;">​</a></h2>',3),R=i('<div><div class="main-verse"><h1 class="verse-arabic">وَقَالَ نُوحٌ رَّبِّ لَا تَذَرْ عَلَى ٱلْأَرْضِ مِنَ ٱلْكَـٰفِرِينَ دَيَّارًا ٢٦</h1></div><p>Noe povedal: „Pane môj! Nenechaj na zemi nikoho z tých, ktorí odmietli veriť“,</p></div><div class="break"></div><h2 id="_27" tabindex="-1">27 <a class="header-anchor" href="#_27" aria-label="Permalink to &quot;27&quot;">​</a></h2>',3),E=i('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّكَ إِن تَذَرْهُمْ يُضِلُّوا عِبَادَكَ وَلَا يَلِدُوٓا إِلَّا فَاجِرًا كَفَّارًا ٢٧</h1></div><p>„Ak by si ich nechal (na zemi), uvedú do bludu tých, ktorí sa ti odovzdali a nebudú rodiť nič iné než hriešnikov odmietajúcich vieru“,</p></div><div class="break"></div><h2 id="_28" tabindex="-1">28 <a class="header-anchor" href="#_28" aria-label="Permalink to &quot;28&quot;">​</a></h2>',3),B=o("div",null,[o("div",{class:"main-verse"},[o("h1",{class:"verse-arabic"},"رَّبِّ ٱغْفِرْ لِى وَلِوَٰلِدَىَّ وَلِمَن دَخَلَ بَيْتِىَ مُؤْمِنًا وَلِلْمُؤْمِنِينَ وَٱلْمُؤْمِنَـٰتِ وَلَا تَزِدِ ٱلظَّـٰلِمِينَ إِلَّا تَبَارًا ٢٨")]),o("p",null,"„Pane môj! Odpusti mi i mojim rodičom i tomu, kto vošiel do môjho domu ako veriaci (uveril v to, čo som oznamoval) i (odpusť) veriacim mužom i veriacim ženám a nepridaj krivdiacim nič iné len zničenie (v živote pozemskom a stratu dobra v živote večnom)“.")],-1);function O(J,K,M,w,$,H){const a=_("Badge");return d(),t("div",null,[r,e(a,{type:"info",text:"71:1",class:"badge"}),l,e(a,{type:"info",text:"71:2",class:"badge"}),n,e(a,{type:"info",text:"71:3",class:"badge"}),h,e(a,{type:"info",text:"71:4",class:"badge"}),c,e(a,{type:"info",text:"71:5",class:"badge"}),m,e(a,{type:"info",text:"71:6",class:"badge"}),b,e(a,{type:"info",text:"71:7",class:"badge"}),p,e(a,{type:"info",text:"71:8",class:"badge"}),T,e(a,{type:"info",text:"71:9",class:"badge"}),u,e(a,{type:"info",text:"71:10",class:"badge"}),k,e(a,{type:"info",text:"71:11",class:"badge"}),P,e(a,{type:"info",text:"71:12",class:"badge"}),A,e(a,{type:"info",text:"71:13",class:"badge"}),S,e(a,{type:"info",text:"71:14",class:"badge"}),y,e(a,{type:"info",text:"71:15",class:"badge"}),f,e(a,{type:"info",text:"71:16",class:"badge"}),V,e(a,{type:"info",text:"71:17",class:"badge"}),j,e(a,{type:"info",text:"71:18",class:"badge"}),q,e(a,{type:"info",text:"71:19",class:"badge"}),x,e(a,{type:"info",text:"71:20",class:"badge"}),C,e(a,{type:"info",text:"71:21",class:"badge"}),I,e(a,{type:"info",text:"71:22",class:"badge"}),z,e(a,{type:"info",text:"71:23",class:"badge"}),N,e(a,{type:"info",text:"71:24",class:"badge"}),g,e(a,{type:"info",text:"71:25",class:"badge"}),D,e(a,{type:"info",text:"71:26",class:"badge"}),R,e(a,{type:"info",text:"71:27",class:"badge"}),E,e(a,{type:"info",text:"71:28",class:"badge"}),B])}const G=s(v,[["render",O]]);export{F as __pageData,G as default};
