import{_ as s,D as t,c as d,I as e,a4 as o,j as i,o as r}from"./chunks/framework.BalxvVNQ.js";const K=JSON.parse(`{"title":"Kur'an","titleTemplate":false,"description":"","frontmatter":{"outline":"deep","title":"Kur'an","titleTemplate":false},"headers":[],"relativePath":"tr/quran/73.md","filePath":"tr/quran/73.md"}`),l={name:"tr/quran/73.md"},n=o('<div class="chapter-title-wrapper"><div class="chapter-title">73</div><div class="chapter-title-slovak">Rúchom zakrytý</div><div class="chapter-opening">115</div><div class="chapter-opening-slovak">Rahman ve Rahim olan Allah&#39;ın adıyla</div></div><div class="intro2-wrapper"><div class="chapter-info-wrapper"><div class="chapter-info-translation">Meali</div><div class="chapter-info-name">Diyanet Vakfı Meali</div></div></div><h2 id="_1" tabindex="-1">1 <a class="header-anchor" href="#_1" aria-label="Permalink to &quot;1&quot;">​</a></h2>',3),v=o('<div><div class="main-verse"><h1 class="verse-arabic">يَـٰٓأَيُّهَا ٱلْمُزَّمِّلُ ١</h1></div><p>Ty rúchom zakrytý.</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Rúchom zakrytý sa myslí prorok Muhammad. Prorok je tu označený ako rúchom zakrytý v súvislosti s príhodou, ktorá sa mu prihodila v počiatkoch posolstva. Celá príhoda je uvedená vo výklade k veršu 74:5 vo vydaní prekladu Koránu s výkladom.</p></details><div class="break"></div><h2 id="_2" tabindex="-1">2 <a class="header-anchor" href="#_2" aria-label="Permalink to &quot;2&quot;">​</a></h2>',4),_=o('<div><div class="main-verse"><h1 class="verse-arabic">قُمِ ٱلَّيْلَ إِلَّا قَلِيلًا ٢</h1></div><p>Ostaň hore v noci modliac sa, okrem malej časti z nej (nechaj spánok, Muhammad, v noci a väčšiu časť z nej sa modli a pripravuj sa na ťažkú úlohu, ktorou si bol poverený, priprav sa na oznámenie tohto nového posolstva ľuďom),</p></div><div class="break"></div><h2 id="_3" tabindex="-1">3 <a class="header-anchor" href="#_3" aria-label="Permalink to &quot;3&quot;">​</a></h2>',3),c=o('<div><div class="main-verse"><h1 class="verse-arabic">نِّصْفَهُۥٓ أَوِ ٱنقُصْ مِنْهُ قَلِيلًا ٣</h1></div><p>Polovicu z nej (modli sa polovicu noci), alebo uber z nej trocha (uber z tejto polovice a modli sa o niečo menej),</p></div><div class="break"></div><h2 id="_4" tabindex="-1">4 <a class="header-anchor" href="#_4" aria-label="Permalink to &quot;4&quot;">​</a></h2>',3),h=o('<div><div class="main-verse"><h1 class="verse-arabic">أَوْ زِدْ عَلَيْهِ وَرَتِّلِ ٱلْقُرْءَانَ تَرْتِيلًا ٤</h1></div><p>Alebo pridaj k nej (modli sa viac než polovicu noci, ak by si chcel a vládal, modli sa ale tak dlho, aby ti to dodalo dostatok trpezlivosti a výdrže) a odriekaj (počas tejto noci) Korán odriekaním pomalým (aby nebol pre teba veľkou záťažou a aby si ho vnímal a cítil jeho slová).</p></div><div class="break"></div><h2 id="_5" tabindex="-1">5 <a class="header-anchor" href="#_5" aria-label="Permalink to &quot;5&quot;">​</a></h2>',3),p=o('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّا سَنُلْقِى عَلَيْكَ قَوْلًا ثَقِيلًا ٥</h1></div><p>My vrhneme na teba slová ťažké (ktoré majú obrovskú váhu a význam, preto sa na prijímanie týchto slov uvedeným spôsobom pripravuj),</p></div><div class="break"></div><h2 id="_6" tabindex="-1">6 <a class="header-anchor" href="#_6" aria-label="Permalink to &quot;6&quot;">​</a></h2>',3),m=o('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ نَاشِئَةَ ٱلَّيْلِ هِىَ أَشَدُّ وَطْـًٔا وَأَقْوَمُ قِيلًا ٦</h1></div><p>Prvé hodiny noci, tie majú väčší dopad (silnejšie pôsobia na dušu človeka, pretože sa všetko práve odobralo na odpočinok a všetko stíchlo) a povedia sa v nich správnejšie slová (keďže sa človek už odpúta od každodenných povinností a problémov, lepšie vníma a jeho myseľ je čistejšia),</p></div><div class="break"></div><h2 id="_7" tabindex="-1">7 <a class="header-anchor" href="#_7" aria-label="Permalink to &quot;7&quot;">​</a></h2>',3),k=o('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ لَكَ فِى ٱلنَّهَارِ سَبْحًا طَوِيلًا ٧</h1></div><p>Vo dne sa o záležitosti svoje dlho staráš, (Staráš sa o zabezpečenie svojej obživy a zárobku. Staráš sa pritom aj o záležitosti ľudí, preto ti veľa času na uctievanie a spomínanie si na Boha neostáva, takže si na to vyhraď časť noci.)</p></div><div class="break"></div><h2 id="_8" tabindex="-1">8 <a class="header-anchor" href="#_8" aria-label="Permalink to &quot;8&quot;">​</a></h2>',3),u=o('<div><div class="main-verse"><h1 class="verse-arabic">وَٱذْكُرِ ٱسْمَ رَبِّكَ وَتَبَتَّلْ إِلَيْهِ تَبْتِيلًا ٨</h1></div><p>Ale spomínaj (aj napriek tomu) meno Pána svojho a k Nemu sa obráť (na čas zanechaj za sebou život pozemský i všetko, čo v ňom je) obrátením úplným (v ktorom budeš mať len Jeho v srdci a v mysli),</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Význam predchádzajúcich veršov je: Až dokončíš svoje každodenné povinnosti, Muhammad, noc príde a všetko sa uloží k spánku. Potom sa už celým svojim srdcom v časti noci venuj už len Bohu, jeho uctievaniu a prednášaniu Koránu.</p></details><div class="break"></div><h2 id="_9" tabindex="-1">9 <a class="header-anchor" href="#_9" aria-label="Permalink to &quot;9&quot;">​</a></h2>',4),b=o('<div><div class="main-verse"><h1 class="verse-arabic">رَّبُّ ٱلْمَشْرِقِ وَٱلْمَغْرِبِ لَآ إِلَـٰهَ إِلَّا هُوَ فَٱتَّخِذْهُ وَكِيلًا ٩</h1></div><p>K Pánovi východu a západu. Niet boha okrem Neho (Boha), preto sa naňho spoľahni,</p></div><div class="break"></div><h2 id="_10" tabindex="-1">10 <a class="header-anchor" href="#_10" aria-label="Permalink to &quot;10&quot;">​</a></h2>',3),T=o('<div><div class="main-verse"><h1 class="verse-arabic">وَٱصْبِرْ عَلَىٰ مَا يَقُولُونَ وَٱهْجُرْهُمْ هَجْرًا جَمِيلًا ١٠</h1></div><p>Buď trpezlivý voči tomu, čo hovoria a opusti ich opustením pekným (nechaj ich a odíď od nich v pokoji a bez toho, aby si im akékoľvek tvrdé slovo povedal),</p></div><div class="break"></div><h2 id="_11" tabindex="-1">11 <a class="header-anchor" href="#_11" aria-label="Permalink to &quot;11&quot;">​</a></h2>',3),j=o('<div><div class="main-verse"><h1 class="verse-arabic">وَذَرْنِى وَٱلْمُكَذِّبِينَ أُولِى ٱلنَّعْمَةِ وَمَهِّلْهُمْ قَلِيلًا ١١</h1></div><p>Nechaj Ma (Boha, Muhammad) s tými, ktorí za lož označujú (čo som zoslal), ktorým sa dary dostali (dary Mnou ľuďom určené - bohatstvo a dostatok všetkého) a počkaj na nich trocha (Ja sa o nich i o to, čo som im dal, postarám),</p></div><div class="break"></div><h2 id="_12" tabindex="-1">12 <a class="header-anchor" href="#_12" aria-label="Permalink to &quot;12&quot;">​</a></h2>',3),y=o('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ لَدَيْنَآ أَنكَالًا وَجَحِيمًا ١٢</h1></div><p>U nás sú okovy a peklo (pripravené pre všetkých tých, ktorí veriť odmietajú a zlu sa oddávajú)</p></div><div class="break"></div><h2 id="_13" tabindex="-1">13 <a class="header-anchor" href="#_13" aria-label="Permalink to &quot;13&quot;">​</a></h2>',3),P=o('<div><div class="main-verse"><h1 class="verse-arabic">وَطَعَامًا ذَا غُصَّةٍ وَعَذَابًا أَلِيمًا ١٣</h1></div><p>A jedlo, ktoré dusí (zastaví sa v hrdle a nedá sa prehltnúť) i trápenie bolestivé.</p></div><div class="break"></div><h2 id="_14" tabindex="-1">14 <a class="header-anchor" href="#_14" aria-label="Permalink to &quot;14&quot;">​</a></h2>',3),z=o('<div><div class="main-verse"><h1 class="verse-arabic">يَوْمَ تَرْجُفُ ٱلْأَرْضُ وَٱلْجِبَالُ وَكَانَتِ ٱلْجِبَالُ كَثِيبًا مَّهِيلًا ١٤</h1></div><p>V deň, keď sa zem a hory otrasú a hory ostanú ako pohyblivý piesok (vtedy príde čas tých, ktorí veriť odmietali, nastane deň zmŕtvychvstania a súdny deň).</p></div><div class="break"></div><h2 id="_15" tabindex="-1">15 <a class="header-anchor" href="#_15" aria-label="Permalink to &quot;15&quot;">​</a></h2>',3),A=o('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّآ أَرْسَلْنَآ إِلَيْكُمْ رَسُولًا شَـٰهِدًا عَلَيْكُمْ كَمَآ أَرْسَلْنَآ إِلَىٰ فِرْعَوْنَ رَسُولًا ١٥</h1></div><p>My sme k vám poslali posla ako svedka svedčiaceho o vás (bude svedkom toho, aký postoj ste zaujali voči tomu, čo sme vám zoslali), podobne ako sme poslali k Faraónovi posla (ktorý sa stal svedkom toho, ako sa Faraón k posolstvu zachoval),</p></div><div class="break"></div><h2 id="_16" tabindex="-1">16 <a class="header-anchor" href="#_16" aria-label="Permalink to &quot;16&quot;">​</a></h2>',3),S=o('<div><div class="main-verse"><h1 class="verse-arabic">فَعَصَىٰ فِرْعَوْنُ ٱلرَّسُولَ فَأَخَذْنَـٰهُ أَخْذًا وَبِيلًا ١٦</h1></div><p>Faraón však neposlúchol posla (a posolstvo, s ktorým sme tohto posla k nemu poslali), preto sme ho vzali (zo života pozemského) vzatím so zlým následkom preňho.</p></div><div class="break"></div><h2 id="_17" tabindex="-1">17 <a class="header-anchor" href="#_17" aria-label="Permalink to &quot;17&quot;">​</a></h2>',3),f=o('<div><div class="main-verse"><h1 class="verse-arabic">فَكَيْفَ تَتَّقُونَ إِن كَفَرْتُمْ يَوْمًا يَجْعَلُ ٱلْوِلْدَٰنَ شِيبًا ١٧</h1></div><p>Pokiaľ by ste odmietli veriť, ako sa vyhnete dňu (zmŕtvychvstania a súdnemu dňu), ktorý učiní deti šedivými (v ktorom aj deti zošedivejú od strachu pred tým, čo sa bude diať)?</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Adresátmi sú tu tí z kmeňa Qurejš, z obyvateľov Mekky, ktorí odmietali veriť v posolstvo, ktoré Boh prostredníctvom svojho posla Muhammada ľuďom zoslal.</p></details><div class="break"></div><h2 id="_18" tabindex="-1">18 <a class="header-anchor" href="#_18" aria-label="Permalink to &quot;18&quot;">​</a></h2>',4),V=o('<div><div class="main-verse"><h1 class="verse-arabic">ٱلسَّمَآءُ مُنفَطِرٌۢ بِهِۦ ۚ كَانَ وَعْدُهُۥ مَفْعُولًا ١٨</h1></div><p>Nebo bude roztrhnuté v ňom (v spomenutom dni). Jeho (Boží) sľub (o príchode tohto dňa) sa naplnil.</p></div><div class="break"></div><h2 id="_19" tabindex="-1">19 <a class="header-anchor" href="#_19" aria-label="Permalink to &quot;19&quot;">​</a></h2>',3),q=o('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ هَـٰذِهِۦ تَذْكِرَةٌ ۖ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلًا ١٩</h1></div><p>Toto (čo je v Koráne zoslané) je pripomenutie (pre stvorených). Kto bude chcieť, ten na cestu k Pánovi svojmu vykročí.</p></div><div class="break"></div><h2 id="_20" tabindex="-1">20 <a class="header-anchor" href="#_20" aria-label="Permalink to &quot;20&quot;">​</a></h2>',3),x=i("div",null,[i("div",{class:"main-verse"},[i("h1",{class:"verse-arabic"},"۞ إِنَّ رَبَّكَ يَعْلَمُ أَنَّكَ تَقُومُ أَدْنَىٰ مِن ثُلُثَىِ ٱلَّيْلِ وَنِصْفَهُۥ وَثُلُثَهُۥ وَطَآئِفَةٌ مِّنَ ٱلَّذِينَ مَعَكَ ۚ وَٱللَّهُ يُقَدِّرُ ٱلَّيْلَ وَٱلنَّهَارَ ۚ عَلِمَ أَن لَّن تُحْصُوهُ فَتَابَ عَلَيْكُمْ ۖ فَٱقْرَءُوا مَا تَيَسَّرَ مِنَ ٱلْقُرْءَانِ ۚ عَلِمَ أَن سَيَكُونُ مِنكُم مَّرْضَىٰ ۙ وَءَاخَرُونَ يَضْرِبُونَ فِى ٱلْأَرْضِ يَبْتَغُونَ مِن فَضْلِ ٱللَّهِ ۙ وَءَاخَرُونَ يُقَـٰتِلُونَ فِى سَبِيلِ ٱللَّهِ ۖ فَٱقْرَءُوا مَا تَيَسَّرَ مِنْهُ ۚ وَأَقِيمُوا ٱلصَّلَوٰةَ وَءَاتُوا ٱلزَّكَوٰةَ وَأَقْرِضُوا ٱللَّهَ قَرْضًا حَسَنًا ۚ وَمَا تُقَدِّمُوا لِأَنفُسِكُم مِّنْ خَيْرٍ تَجِدُوهُ عِندَ ٱللَّهِ هُوَ خَيْرًا وَأَعْظَمَ أَجْرًا ۚ وَٱسْتَغْفِرُوا ٱللَّهَ ۖ إِنَّ ٱللَّهَ غَفُورٌ رَّحِيمٌ ٢٠")]),i("p",null,"Pán tvoj (Muhammad) vie, že ostávaš hore (v noci, uctievajúc Boha) o niečo menej ako dve tretiny noci, (niekedy o niečo menej ako) jej polovicu a (inokedy o niečo menej ako) jej tretinu a hore ostáva aj skupina z radov tých (veriacich), ktorí sú s tebou (časť veriacich, ktorí uverili v to, čo Boh zoslal ostáva tiež v noci hore a k Bohu sa modlí). Boh stanovuje noc a deň (ich dĺžku a čo k nim náleží). Vedel, že to nespočítate (že nedokážete vyrátať presne, kedy uplynú dve tretiny noci alebo polovica noci alebo jej tretina), preto prijal od vás pokánie (za to a túto povinnosť vám zmiernil). Prednášajte, koľko sa dá z Koránu (čítajte z neho a prednášajte, koľko budete vládať). Vedel (Boh), že budú medzi vami chorí a ďalší budú cestovať po zemi, sledujúc (a snažiac sa o) Božie dobrodenie a (budú i) ďalší (ktorí) budú bojovať za cestu, ktorú Boh určil. Prednášajte, koľko sa z neho (Koránu) dá. Konajte modlitbu a dávajte zakat a poskytnite Bohu dobrú pôžičku (rozdávajte zo svojich majetkov na účely, ktoré Boh určil, aby ste tým získali Božiu spokojnosť, pozri napríklad verš 9:60). Akékoľvek dobro, ktoré by ste vykonali pre svoje duše (najmä druhy dobrých konaní hore uvedených), nájdete u Boha (za dobro, ktoré vykonáte v živote spokojnosť tým dosiahnete). Proste Boha o odpustenie. Veď Boh je odpúšťajúci a milostivý.")],-1),C=i("details",{class:"details custom-block"},[i("summary",null,"Tafsir"),i("p",null,"Boh vedel, že nedokážete vyrátať presne, kedy uplynú dve tretiny noci alebo polovica noci alebo jej tretina, preto prijal od vás pokánie za to a túto povinnosť vám zmiernil."),i("p",null,"Tento verš bol zoslaný potom, ako boli zoslané verše 2 až 4 tejto kapitoly. Muslimovia chceli spomenuté verše dodržať a modliť sa aj časť noci, ale nevedeli, kedy presne uplynie polovica noci alebo jej tretina. Preto ostávali hore modliť sa, kým nezačalo svitať, z obavy, aby sa nepomýlili. To ich ale vyčerpávalo, až im nohy opuchli a oni zbledli. Preto Boh zoslal tento verš, v ktorom im hovorí, že Boh vie, že nedokážu vypočítať, kedy presne nastane polovica alebo tretina noci a ponechal modlenie počas noci na individuálne možnosti modliacich sa.")],-1);function I(N,B,g,R,D,E){const a=t("Badge");return r(),d("div",null,[n,e(a,{type:"info",text:"73:1",class:"badge"}),v,e(a,{type:"info",text:"73:2",class:"badge"}),_,e(a,{type:"info",text:"73:3",class:"badge"}),c,e(a,{type:"info",text:"73:4",class:"badge"}),h,e(a,{type:"info",text:"73:5",class:"badge"}),p,e(a,{type:"info",text:"73:6",class:"badge"}),m,e(a,{type:"info",text:"73:7",class:"badge"}),k,e(a,{type:"info",text:"73:8",class:"badge"}),u,e(a,{type:"info",text:"73:9",class:"badge"}),b,e(a,{type:"info",text:"73:10",class:"badge"}),T,e(a,{type:"info",text:"73:11",class:"badge"}),j,e(a,{type:"info",text:"73:12",class:"badge"}),y,e(a,{type:"info",text:"73:13",class:"badge"}),P,e(a,{type:"info",text:"73:14",class:"badge"}),z,e(a,{type:"info",text:"73:15",class:"badge"}),A,e(a,{type:"info",text:"73:16",class:"badge"}),S,e(a,{type:"info",text:"73:17",class:"badge"}),f,e(a,{type:"info",text:"73:18",class:"badge"}),V,e(a,{type:"info",text:"73:19",class:"badge"}),q,e(a,{type:"info",text:"73:20",class:"badge"}),x,C])}const J=s(l,[["render",I]]);export{K as __pageData,J as default};
