import{_ as o,D as _,c as t,I as e,a2 as s,j as i,o as d}from"./chunks/framework.-9P5VKzf.js";const M=JSON.parse(`{"title":"Kur'an","titleTemplate":false,"description":"","frontmatter":{"outline":"deep","title":"Kur'an","titleTemplate":false},"headers":[],"relativePath":"tr/quran/84.md","filePath":"tr/quran/84.md"}`),r={name:"tr/quran/84.md"},v=s('<div class="chapter-title-wrapper"><div class="chapter-title">84</div><div class="chapter-title-slovak">Roztrhnutie</div><div class="chapter-opening">115</div><div class="chapter-opening-slovak">Rahman ve Rahim olan Allah&#39;ın adıyla</div></div><div class="intro2-wrapper"><div class="chapter-info-wrapper"><div class="chapter-info-translation">Meali</div><div class="chapter-info-name">Diyanet Vakfı Meali</div></div></div><h2 id="_1" tabindex="-1">1 <a class="header-anchor" href="#_1" aria-label="Permalink to &quot;1&quot;">​</a></h2>',3),n=s('<div><div class="main-verse"><h1 class="verse-arabic">إِذَا ٱلسَّمَآءُ ٱنشَقَّتْ ١</h1></div><p>Až sa nebo roztrhne,</p></div><div class="break"></div><h2 id="_2" tabindex="-1">2 <a class="header-anchor" href="#_2" aria-label="Permalink to &quot;2&quot;">​</a></h2>',3),l=s('<div><div class="main-verse"><h1 class="verse-arabic">وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ ٢</h1></div><p>A vypočuje (a poslúchne) Pána svojho, čo je povinné (urobiť),</p></div><div class="break"></div><h2 id="_3" tabindex="-1">3 <a class="header-anchor" href="#_3" aria-label="Permalink to &quot;3&quot;">​</a></h2>',3),h=s('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَا ٱلْأَرْضُ مُدَّتْ ٣</h1></div><p>A až sa zem rozprestrie (a zmiznú z jej povrchu akékoľvek hory alebo výšiny),</p></div><div class="break"></div><h2 id="_4" tabindex="-1">4 <a class="header-anchor" href="#_4" aria-label="Permalink to &quot;4&quot;">​</a></h2>',3),c=s('<div><div class="main-verse"><h1 class="verse-arabic">وَأَلْقَتْ مَا فِيهَا وَتَخَلَّتْ ٤</h1></div><p>A (zem) odhodí (a vyvrhne), čo je v nej a opustí to (všetko, čo v nej bolo dovtedy skryté, sa objaví),</p></div><div class="break"></div><h2 id="_5" tabindex="-1">5 <a class="header-anchor" href="#_5" aria-label="Permalink to &quot;5&quot;">​</a></h2>',3),p=s('<div><div class="main-verse"><h1 class="verse-arabic">وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ ٥</h1></div><p>A vypočuje (a poslúchne) Pána svojho, čo je povinné (urobiť).</p></div><div class="break"></div><h2 id="_6" tabindex="-1">6 <a class="header-anchor" href="#_6" aria-label="Permalink to &quot;6&quot;">​</a></h2>',3),m=s('<div><div class="main-verse"><h1 class="verse-arabic">يَـٰٓأَيُّهَا ٱلْإِنسَـٰنُ إِنَّكَ كَادِحٌ إِلَىٰ رَبِّكَ كَدْحًا فَمُلَـٰقِيهِ ٦</h1></div><p>Človek! Ty s úsilím svojím smeruješ k Pánovi svojmu, so všetkým úsilím (dobrým i zlým) a stretneš Ho.</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Verš môže mať dva významy. Prvý, že človek sa snaží a v súdny deň uvidí výsledok tejto svojej snahy a úsilia. Druhý, že po tomto úsilí, človek, ktorého úsilie bolo úspešné, uvidí Pána svojho.</p></details><div class="break"></div><h2 id="_7" tabindex="-1">7 <a class="header-anchor" href="#_7" aria-label="Permalink to &quot;7&quot;">​</a></h2>',4),T=s('<div><div class="main-verse"><h1 class="verse-arabic">فَأَمَّا مَنْ أُوتِىَ كِتَـٰبَهُۥ بِيَمِينِهِۦ ٧</h1></div><p>Ten, komu bude daná jeho kniha (skutkov) do pravice jeho,</p></div><div class="break"></div><h2 id="_8" tabindex="-1">8 <a class="header-anchor" href="#_8" aria-label="Permalink to &quot;8&quot;">​</a></h2>',3),b=s('<div><div class="main-verse"><h1 class="verse-arabic">فَسَوْفَ يُحَاسَبُ حِسَابًا يَسِيرًا ٨</h1></div><p>S tým bude zúčtovane účtovaním ľahkým (účtovanie s takýmto človekom bude preň ľahké, pretože jeho zbožnosť a dobré skutky prevýšia tie zlé),</p></div><div class="break"></div><h2 id="_9" tabindex="-1">9 <a class="header-anchor" href="#_9" aria-label="Permalink to &quot;9&quot;">​</a></h2>',3),k=s('<div><div class="main-verse"><h1 class="verse-arabic">وَيَنقَلِبُ إِلَىٰ أَهْلِهِۦ مَسْرُورًا ٩</h1></div><p>A vráti sa k rodine svojej potešený.</p></div><div class="break"></div><h2 id="_10" tabindex="-1">10 <a class="header-anchor" href="#_10" aria-label="Permalink to &quot;10&quot;">​</a></h2>',3),u=s('<div><div class="main-verse"><h1 class="verse-arabic">وَأَمَّا مَنْ أُوتِىَ كِتَـٰبَهُۥ وَرَآءَ ظَهْرِهِۦ ١٠</h1></div><p>Avšak ten, komu bude daná jeho kniha (skutkov) spoza jeho chrbta,</p></div><div class="break"></div><h2 id="_11" tabindex="-1">11 <a class="header-anchor" href="#_11" aria-label="Permalink to &quot;11&quot;">​</a></h2>',3),A=s('<div><div class="main-verse"><h1 class="verse-arabic">فَسَوْفَ يَدْعُوا ثُبُورًا ١١</h1></div><p>Ten bude prosiť o záhubu (aby bol zahubený, zomrel a vyhol sa tomu, čo vidí a čo ho neminie),</p></div><div class="break"></div><h2 id="_12" tabindex="-1">12 <a class="header-anchor" href="#_12" aria-label="Permalink to &quot;12&quot;">​</a></h2>',3),P=s('<div><div class="main-verse"><h1 class="verse-arabic">وَيَصْلَىٰ سَعِيرًا ١٢</h1></div><p>A bude horieť v planúcom ohni,</p></div><div class="break"></div><h2 id="_13" tabindex="-1">13 <a class="header-anchor" href="#_13" aria-label="Permalink to &quot;13&quot;">​</a></h2>',3),S=s('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّهُۥ كَانَ فِىٓ أَهْلِهِۦ مَسْرُورًا ١٣</h1></div><p>On žil (predtým v živote pozemskom) vo svojej rodine v radosti (žil bezstarostným a radostným životom, odmietajúc vieru i to, čo Boh uložil a nebral do úvahy varovania, ktoré varovali pred týmto dňom),</p></div><div class="break"></div><h2 id="_14" tabindex="-1">14 <a class="header-anchor" href="#_14" aria-label="Permalink to &quot;14&quot;">​</a></h2>',3),f=s('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّهُۥ ظَنَّ أَن لَّن يَحُورَ ١٤</h1></div><p>On (takýto človek) si myslel, že sa (k Pánovi svojmu) nenavráti.</p></div><div class="break"></div><h2 id="_15" tabindex="-1">15 <a class="header-anchor" href="#_15" aria-label="Permalink to &quot;15&quot;">​</a></h2>',3),y=s('<div><div class="main-verse"><h1 class="verse-arabic">بَلَىٰ إِنَّ رَبَّهُۥ كَانَ بِهِۦ بَصِيرًا ١٥</h1></div><p>Ale áno (on sa k Nemu vráti a bude vzkriesený). Jeho Pán vidí všetko, čo sa ho týka (a čo robí).</p></div><div class="break"></div><h2 id="_16" tabindex="-1">16 <a class="header-anchor" href="#_16" aria-label="Permalink to &quot;16&quot;">​</a></h2>',3),V=s('<div><div class="main-verse"><h1 class="verse-arabic">فَلَآ أُقْسِمُ بِٱلشَّفَقِ ١٦</h1></div><p>Prisahám (Boh prisahá) na súmrak,</p></div><div class="break"></div><h2 id="_17" tabindex="-1">17 <a class="header-anchor" href="#_17" aria-label="Permalink to &quot;17&quot;">​</a></h2>',3),q=s('<div><div class="main-verse"><h1 class="verse-arabic">وَٱلَّيْلِ وَمَا وَسَقَ ١٧</h1></div><p>A na noc a na všetko, čo ona pojala (a zahalila svojou temnotou),</p></div><div class="break"></div><h2 id="_18" tabindex="-1">18 <a class="header-anchor" href="#_18" aria-label="Permalink to &quot;18&quot;">​</a></h2>',3),x=s('<div><div class="main-verse"><h1 class="verse-arabic">وَٱلْقَمَرِ إِذَا ٱتَّسَقَ ١٨</h1></div><p>A na spln mesiaca (keď je mesiac v splne okrúhly a plný),</p></div><div class="break"></div><h2 id="_19" tabindex="-1">19 <a class="header-anchor" href="#_19" aria-label="Permalink to &quot;19&quot;">​</a></h2>',3),j=s('<div><div class="main-verse"><h1 class="verse-arabic">لَتَرْكَبُنَّ طَبَقًا عَن طَبَقٍ ١٩</h1></div><p>Že nastúpite etapu po etape (a stav po stave a nimi prejdete).</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Z hľadiska významu tohto verša nájdeme vo výkladoch Koránu niekoľko možných výkladov. Najznámejší z nich hovorí, že sa tu myslí človek, ktorý prechádza rôznymi etapami od chvíle, kedy je počatý, až kým nezomrie a nebude vzkriesený. Ďalší výklad hovorí, že sa tým myslia stavy v deň zmŕtvychvstania a súdny deň.</p></details><div class="break"></div><h2 id="_20" tabindex="-1">20 <a class="header-anchor" href="#_20" aria-label="Permalink to &quot;20&quot;">​</a></h2>',4),C=s('<div><div class="main-verse"><h1 class="verse-arabic">فَمَا لَهُمْ لَا يُؤْمِنُونَ ٢٠</h1></div><p>Čo je im (ľuďom), že neveria (napriek všetkým týmto jasným varovaniam)?</p></div><div class="break"></div><h2 id="_21" tabindex="-1">21 <a class="header-anchor" href="#_21" aria-label="Permalink to &quot;21&quot;">​</a></h2>',3),I=s('<div><div class="main-verse"><h1 class="verse-arabic">وَإِذَا قُرِئَ عَلَيْهِمُ ٱلْقُرْءَانُ لَا يَسْجُدُونَ ۩ ٢١</h1></div><p>A keď je im prednesený Korán, do polohy sužúdu (sužúd je poklona, v ktorej sa človek pokloní, kľakne na kolená, nakloní trup a hlavu smerom k zemi, spojí čelo a nos so zemou a položí ruky ku hlave) sa nesklonia (a neprejavia tak úctu a rešpektu Bohu najvyššiemu)?</p></div><div class="break"></div><h2 id="_22" tabindex="-1">22 <a class="header-anchor" href="#_22" aria-label="Permalink to &quot;22&quot;">​</a></h2>',3),z=s('<div><div class="main-verse"><h1 class="verse-arabic">بَلِ ٱلَّذِينَ كَفَرُوا يُكَذِّبُونَ ٢٢</h1></div><p>Tí ktorí odmietajú veriť, ho (Korán) za lož označujú (miesto toho, aby nad slovami v ňom zoslanými premýšľali a vážne ich brali),</p></div><div class="break"></div><h2 id="_23" tabindex="-1">23 <a class="header-anchor" href="#_23" aria-label="Permalink to &quot;23&quot;">​</a></h2>',3),N=s('<div><div class="main-verse"><h1 class="verse-arabic">وَٱللَّهُ أَعْلَمُ بِمَا يُوعُونَ ٢٣</h1></div><p>Boh pritom lepšie vie, čo skrývajú (vo svojich srdciach),</p></div><div class="break"></div><h2 id="_24" tabindex="-1">24 <a class="header-anchor" href="#_24" aria-label="Permalink to &quot;24&quot;">​</a></h2>',3),g=s('<div><div class="main-verse"><h1 class="verse-arabic">فَبَشِّرْهُم بِعَذَابٍ أَلِيمٍ ٢٤</h1></div><p>Oznám im radostnú zvesť o trápení bolestivom. (Nech sa v živote pozemskom takýto človek teší a raduje z toho, čo koná, ale nech vie, že až nastane čas, bude donekonečna ľutovať, ako sa voči poznaniu zoslanom v Koráne zachoval.)</p></div><div class="break"></div><h2 id="_25" tabindex="-1">25 <a class="header-anchor" href="#_25" aria-label="Permalink to &quot;25&quot;">​</a></h2>',3),D=i("div",null,[i("div",{class:"main-verse"},[i("h1",{class:"verse-arabic"},"إِلَّا ٱلَّذِينَ ءَامَنُوا وَعَمِلُوا ٱلصَّـٰلِحَـٰتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ ٢٥")]),i("p",null,"Tým, ktorí uverili a dobré skutky konali, sa dostane odmena (úplná a) nekonečná (tých sa toto trápenie nedotkne).")],-1);function R(E,B,K,O,$,w){const a=_("Badge");return d(),t("div",null,[v,e(a,{type:"info",text:"84:1",class:"badge"}),n,e(a,{type:"info",text:"84:2",class:"badge"}),l,e(a,{type:"info",text:"84:3",class:"badge"}),h,e(a,{type:"info",text:"84:4",class:"badge"}),c,e(a,{type:"info",text:"84:5",class:"badge"}),p,e(a,{type:"info",text:"84:6",class:"badge"}),m,e(a,{type:"info",text:"84:7",class:"badge"}),T,e(a,{type:"info",text:"84:8",class:"badge"}),b,e(a,{type:"info",text:"84:9",class:"badge"}),k,e(a,{type:"info",text:"84:10",class:"badge"}),u,e(a,{type:"info",text:"84:11",class:"badge"}),A,e(a,{type:"info",text:"84:12",class:"badge"}),P,e(a,{type:"info",text:"84:13",class:"badge"}),S,e(a,{type:"info",text:"84:14",class:"badge"}),f,e(a,{type:"info",text:"84:15",class:"badge"}),y,e(a,{type:"info",text:"84:16",class:"badge"}),V,e(a,{type:"info",text:"84:17",class:"badge"}),q,e(a,{type:"info",text:"84:18",class:"badge"}),x,e(a,{type:"info",text:"84:19",class:"badge"}),j,e(a,{type:"info",text:"84:20",class:"badge"}),C,e(a,{type:"info",text:"84:21",class:"badge"}),I,e(a,{type:"info",text:"84:22",class:"badge"}),z,e(a,{type:"info",text:"84:23",class:"badge"}),N,e(a,{type:"info",text:"84:24",class:"badge"}),g,e(a,{type:"info",text:"84:25",class:"badge"}),D])}const H=o(r,[["render",R]]);export{M as __pageData,H as default};
