import{_ as i,D as t,c as r,I as o,a2 as e,j as s,o as l}from"./chunks/framework.-9P5VKzf.js";const x=JSON.parse(`{"title":"Kur'an","titleTemplate":false,"description":"","frontmatter":{"outline":"deep","title":"Kur'an","titleTemplate":false},"headers":[],"relativePath":"tr/quran/93.md","filePath":"tr/quran/93.md"}`),d={name:"tr/quran/93.md"},n=e('<div class="chapter-title-wrapper"><div class="chapter-title">93</div><div class="chapter-title-slovak">Predpoludnie</div><div class="chapter-opening">115</div><div class="chapter-opening-slovak">Rahman ve Rahim olan Allah&#39;ın adıyla</div></div><div class="intro2-wrapper"><div class="chapter-info-wrapper"><div class="chapter-info-translation">Meali</div><div class="chapter-info-name">Diyanet Vakfı Meali</div></div></div><h2 id="_1" tabindex="-1">1 <a class="header-anchor" href="#_1" aria-label="Permalink to &quot;1&quot;">​</a></h2>',3),v=e('<div><div class="main-verse"><h1 class="verse-arabic">وَٱلضُّحَىٰ ١</h1></div><p>Prisahám (Boh prisahá) na predpoludnie (na ráno – na prvé hodiny dňa),</p></div><div class="break"></div><h2 id="_2" tabindex="-1">2 <a class="header-anchor" href="#_2" aria-label="Permalink to &quot;2&quot;">​</a></h2>',3),_=e('<div><div class="main-verse"><h1 class="verse-arabic">وَٱلَّيْلِ إِذَا سَجَىٰ ٢</h1></div><p>A na noc, až všetko zakryje (a usadí sa),</p></div><div class="break"></div><h2 id="_3" tabindex="-1">3 <a class="header-anchor" href="#_3" aria-label="Permalink to &quot;3&quot;">​</a></h2>',3),h=e('<div><div class="main-verse"><h1 class="verse-arabic">مَا وَدَّعَكَ رَبُّكَ وَمَا قَلَىٰ ٣</h1></div><p>Tvoj Pán (Muhammad) ťa neopustil a ani si ťa neprestal všímať (stále je s tebou, ochraňuje ťa a o všetkom vie),</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Adresátom je tu prorok Muhammad (p.). Táto časť kapitoly bola zoslaná ako odpoveď tým, ktorí neznášali posla a proroka Muhammada, keď povedali, že posla a proroka Muhammada (p.) opustil jeho Pán. Bolo to následkom toho, že po určitý čas anjel Gabriel za poslom Muhammadom neprišiel. Keď tí z Mekky, ktorí odmietali veriť, tak povedali, bola zoslaná táto kapitola, ako odpoveď na ich slová.</p></details><div class="break"></div><h2 id="_4" tabindex="-1">4 <a class="header-anchor" href="#_4" aria-label="Permalink to &quot;4&quot;">​</a></h2>',4),p=e('<div><div class="main-verse"><h1 class="verse-arabic">وَلَلْـَٔاخِرَةُ خَيْرٌ لَّكَ مِنَ ٱلْأُولَىٰ ٤</h1></div><p>To posledné (život po smrti alebo to, čo ti dá Boh neskôr, Muhammad) bude pre teba lepšie než to prvé (než život pozemský alebo to, čo ti doposiaľ dal),</p></div><details class="details custom-block"><summary>Tafsir</summary><p>O tom, čo sa myslí pod prvým a posledným, sú dva výklady. Jeden hovorí, že sa tým myslí život pozemský a život večný, ktorý príde po súdnom dni. Druhý výklad zasa hovorí, že sa tým myslí to, čím Boh posla a proroka Muhammada obdaril a považovalo sa to za útechu a zvesť pre posla Muhammada, aby vydržal všetko, čomu je vystavený, pretože Boh mu pripravil niečo omnoho lepšie, čo ešte len príde.</p></details><div class="break"></div><h2 id="_5" tabindex="-1">5 <a class="header-anchor" href="#_5" aria-label="Permalink to &quot;5&quot;">​</a></h2>',4),m=e('<div><div class="main-verse"><h1 class="verse-arabic">وَلَسَوْفَ يُعْطِيكَ رَبُّكَ فَتَرْضَىٰ ٥</h1></div><p>Pán tvoj ti dá (zo všetkého dobra to najlepšie), a budeš (s tým, čo ti dá) spokojný (tak sa neboj, že by ťa Boh opustil).</p></div><div class="break"></div><h2 id="_6" tabindex="-1">6 <a class="header-anchor" href="#_6" aria-label="Permalink to &quot;6&quot;">​</a></h2>',3),c=e('<div><div class="main-verse"><h1 class="verse-arabic">أَلَمْ يَجِدْكَ يَتِيمًا فَـَٔاوَىٰ ٦</h1></div><p>A či ťa (Muhammad) nenašiel (Boh) osirelého a nedal ti útočište?</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Adresátom je tu posol a prorok Muhammad (p.). Prorokov otec zomrel, keď on bol ešte v lone svojej matky. Po šiestich rokoch od jeho narodenia mu zomrela aj matka. Do opatery si ho potom vzal jeho starý otec Abdul Muttaleb a opatroval ho až do svojej smrti. Keď mal prorok Muhammad (p.) osem rokov, zomrel aj jeho starý otec. Nato sa ho ujal jeho strýko Abú Táleb, ktorý sa oňho staral a neskôr ho bránil a zastával sa ho. Aj napriek tomu, že Abú Táleb aj po vnuknutí posolstva Muhammadovi naďalej uctieval popri Bohu sochy, bránil Muhammada a zastával sa ho pred ľuďmi (keďže mal vysoké postavenie medzi nimi). Zmysel tohto verša spočíva v tom, že Boh vždy zabezpečil prorokovi Muhammadovi niekoho, kto sa oňho staral až dovtedy, kým nebol zoslaný verš, v ktorom Boh prorokovi oznamuje, že sa už nemá báť nikoho, má oznámiť všetko, čo mu bolo od Boha vnuknuté a Boh sa postará o jeho ochranu. Vtedy prorok požiadal stráž, ktorá ho strážila, aby ho prestala strážiť a odvtedy až do svojej smrti bol prorok bez stráže.</p></details><div class="break"></div><h2 id="_7" tabindex="-1">7 <a class="header-anchor" href="#_7" aria-label="Permalink to &quot;7&quot;">​</a></h2>',4),u=e('<div><div class="main-verse"><h1 class="verse-arabic">وَوَجَدَكَ ضَآلًّا فَهَدَىٰ ٧</h1></div><p>A našiel ťa (Boh, Muhammad) blúdiaceho (hľadajúceho pravdu a premýšľajúceho o Stvoriteľovi, o Bohu), a tak ťa správne usmernil (určil, že nastal čas, aby sa posolstvo začalo napĺňať a aby začalo vnukanie Koránu a tak ťa vrátil spolu s ľuďmi na tú jedinú cestu, ktorú od počiatkov určil),</p></div><div class="break"></div><h2 id="_8" tabindex="-1">8 <a class="header-anchor" href="#_8" aria-label="Permalink to &quot;8&quot;">​</a></h2>',3),k=e('<div><div class="main-verse"><h1 class="verse-arabic">وَوَجَدَكَ عَآئِلًا فَأَغْنَىٰ ٨</h1></div><p>A našiel ťa (Muhammad) živiaceho rodinu (starajúceho sa o živenie a zabezpečenie svojej rodiny) a tak ti dal, čo ti postačí.</p></div><div class="break"></div><h2 id="_9" tabindex="-1">9 <a class="header-anchor" href="#_9" aria-label="Permalink to &quot;9&quot;">​</a></h2>',3),b=e('<div><div class="main-verse"><h1 class="verse-arabic">فَأَمَّا ٱلْيَتِيمَ فَلَا تَقْهَرْ ٩</h1></div><p>Preto sirotu neutláčaj,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Buď k nej dobrý a milý ako láskavý otec a nepoužívaj silu, aby si ju obral o jej práva alebo ponížil. Tento verš i nasledujúci učí ľudí, ako sa majú správať k sirotám a chudobným ľuďom, aby tak prejavili vďaky Bohu za to, čo im dal.</p></details><div class="break"></div><h2 id="_10" tabindex="-1">10 <a class="header-anchor" href="#_10" aria-label="Permalink to &quot;10&quot;">​</a></h2>',4),T=e('<div><div class="main-verse"><h1 class="verse-arabic">وَأَمَّا ٱلسَّآئِلَ فَلَا تَنْهَرْ ١٠</h1></div><p>A toho, kto prosí o pomoc, neokríkaj (a neodháňaj, ak by ťa o pomoc požiadal, ale milým spôsobom sa od neho odvráť, ak mu pomoc nemôžeš alebo z nejakého dôvodu nechceš poskytnúť),</p></div><div class="break"></div><h2 id="_11" tabindex="-1">11 <a class="header-anchor" href="#_11" aria-label="Permalink to &quot;11&quot;">​</a></h2>',3),j=s("div",null,[s("div",{class:"main-verse"},[s("h1",{class:"verse-arabic"},"وَأَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ ١١")]),s("p",null,"A o dare Pána svojho (ľuďom) rozprávaj (rozprávaj im o Bohu, o posolstve, ktoré ti vnukol a poveril ťa ho ľuďom oznámiť a o dobre, ktoré im Korán prináša). (Okrem toho pomáhaj ostatným, čím sa to, čo Boh ukladá, začne prakticky napĺňať.)")],-1);function y(A,P,f,S,z,V){const a=t("Badge");return l(),r("div",null,[n,o(a,{type:"info",text:"93:1",class:"badge"}),v,o(a,{type:"info",text:"93:2",class:"badge"}),_,o(a,{type:"info",text:"93:3",class:"badge"}),h,o(a,{type:"info",text:"93:4",class:"badge"}),p,o(a,{type:"info",text:"93:5",class:"badge"}),m,o(a,{type:"info",text:"93:6",class:"badge"}),c,o(a,{type:"info",text:"93:7",class:"badge"}),u,o(a,{type:"info",text:"93:8",class:"badge"}),k,o(a,{type:"info",text:"93:9",class:"badge"}),b,o(a,{type:"info",text:"93:10",class:"badge"}),T,o(a,{type:"info",text:"93:11",class:"badge"}),j])}const B=i(d,[["render",y]]);export{x as __pageData,B as default};
