import{_ as s,D as t,c as r,I as e,a4 as o,j as i,o as n}from"./chunks/framework.BalxvVNQ.js";const V=JSON.parse(`{"title":"Kur'an","titleTemplate":false,"description":"","frontmatter":{"outline":"deep","title":"Kur'an","titleTemplate":false},"headers":[],"relativePath":"tr/quran/94.md","filePath":"tr/quran/94.md"}`),l={name:"tr/quran/94.md"},d=o('<div class="chapter-title-wrapper"><div class="chapter-title">94</div><div class="chapter-title-slovak">Uvoľnenie</div><div class="chapter-opening">115</div><div class="chapter-opening-slovak">Rahman ve Rahim olan Allah&#39;ın adıyla</div></div><div class="intro2-wrapper"><div class="chapter-info-wrapper"><div class="chapter-info-translation">Meali</div><div class="chapter-info-name">Diyanet Vakfı Meali</div></div></div><h2 id="_1" tabindex="-1">1 <a class="header-anchor" href="#_1" aria-label="Permalink to &quot;1&quot;">​</a></h2>',3),v=o('<div><div class="main-verse"><h1 class="verse-arabic">أَلَمْ نَشْرَحْ لَكَ صَدْرَكَ ١</h1></div><p>A či sme ti (Muhammad) neuvoľnili hruď tvoju (a svetlom múdrosti nenaplnili, aby si prijal vnuknutie Bohom zoslané a mal trpezlivosť a silu znášať ubližovanie a ťažkostí ľudí)?</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Zvestovanie a vnukanie Koránu poslovi a prorokovi Muhammadovi (p.) prostredníctvom anjela Gabriela bolo pre posla Muhammada veľmi náročné a vyčerpávajúce. Veď sám Boh, nad ktorým vyššieho niet, v Koráne hovorí: „Keby sme zoslali dole tento Korán na horu, uvidel by si ju pokornú, popraskanú od strachu pred Bohom“ verš 59:21.</p></details><div class="break"></div><h2 id="_2" tabindex="-1">2 <a class="header-anchor" href="#_2" aria-label="Permalink to &quot;2&quot;">​</a></h2>',4),m=o('<div><div class="main-verse"><h1 class="verse-arabic">وَوَضَعْنَا عَنكَ وِزْرَكَ ٢</h1></div><p>A sňali sme z teba (Muhammad) tvoje bremeno (ktoré ťa zaťažovalo),</p></div><div class="break"></div><h2 id="_3" tabindex="-1">3 <a class="header-anchor" href="#_3" aria-label="Permalink to &quot;3&quot;">​</a></h2>',3),_=o('<div><div class="main-verse"><h1 class="verse-arabic">ٱلَّذِىٓ أَنقَضَ ظَهْرَكَ ٣</h1></div><p>Ktoré ťažilo tvoj chrbát, (Bremenom, ktoré posla a proroka Muhammada zaťažovalo, boli všetky ťažkosti spojené s oznámením posolstva ľuďom a s tým všetkým, čím pritom prechádzal.)</p></div><div class="break"></div><h2 id="_4" tabindex="-1">4 <a class="header-anchor" href="#_4" aria-label="Permalink to &quot;4&quot;">​</a></h2>',3),c=o('<div><div class="main-verse"><h1 class="verse-arabic">وَرَفَعْنَا لَكَ ذِكْرَكَ ٤</h1></div><p>A pozdvihli sme (vysoko) postavenie tvoje.</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Jeden z výrokov proroka Muhammada (p.) hovorí: Prišiel ku mne anjel Gabriel a povedal mi: „Muhammad, tvoj Pán hovorí: Vieš, ako som pozdvihol vysoko tvoje postavenie?“ Povedal som: „Boh, nad ktorým niet vyššieho, vie.“ (Anjel Gabriel) Povedal: „Keď Som (Boh) spomenutý, si spomenutý so mnou“. To sa nám javí napríklad pri volaní k modlitbe, ktoré znie: „Svedčím, že niet boha okrem Boha a svedčím, že Muhammad je Božím poslom ....“, alebo v samotnej modlitbe a pod. Tým sa vlastne postavenie a spomienka na proroka dostala tak vysoko, že keď je spomenutý Boh, je tesne za ním spomenutý aj Muhammad ako posol Boží.</p><p>Táto kapitola bola zoslaná poslovi a prorokovi Muhammadovi (p.) v prvých rokoch islamu, keď bol ešte v Mekke (pred odchodom do Mediny) a v čase, keď boli muslimovia vo veľkej tiesni a keď voči nim ich nepriatelia zostrili boj a nenávisť. Boh pripomína prorokovi (p.), čo všetko mu dal a že jemu i veriacim čoskoro pomôže, len musia byť trpezliví a mať výdrž.</p></details><div class="break"></div><h2 id="_5" tabindex="-1">5 <a class="header-anchor" href="#_5" aria-label="Permalink to &quot;5&quot;">​</a></h2>',4),p=o('<div><div class="main-verse"><h1 class="verse-arabic">فَإِنَّ مَعَ ٱلْعُسْرِ يُسْرًا ٥</h1></div><p>S ťažkosťou (a trápením) prichádza aj uľahčenie (Boh nenechá nikoho v tiesni donekonečna),</p></div><div class="break"></div><h2 id="_6" tabindex="-1">6 <a class="header-anchor" href="#_6" aria-label="Permalink to &quot;6&quot;">​</a></h2>',3),h=o('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ مَعَ ٱلْعُسْرِ يُسْرًا ٦</h1></div><p>S ťažkosťou (a trápením) prichádza aj uľahčenie. (Boh túto skutočnosť znova zvýrazňuje, aby si jej boli ľudia vedomí a aby Ho prosili o pomoc, ochranu a uľahčenie.)</p></div><div class="break"></div><h2 id="_7" tabindex="-1">7 <a class="header-anchor" href="#_7" aria-label="Permalink to &quot;7&quot;">​</a></h2>',3),u=o('<div><div class="main-verse"><h1 class="verse-arabic">فَإِذَا فَرَغْتَ فَٱنصَبْ ٧</h1></div><p>Keď teda skončíš (s tým, čo si robil a s oznamovaním posolstva), tak sa vzpriam,</p></div><div class="break"></div><h2 id="_8" tabindex="-1">8 <a class="header-anchor" href="#_8" aria-label="Permalink to &quot;8&quot;">​</a></h2>',3),k=i("div",null,[i("div",{class:"main-verse"},[i("h1",{class:"verse-arabic"},"وَإِلَىٰ رَبِّكَ فَٱرْغَب ٨")]),i("p",null,"A k Pánovi svojmu spieť chci (s modlením a prosbami svojimi).")],-1),b=i("details",{class:"details custom-block"},[i("summary",null,"Tafsir"),i("p",null,"Význam predchádzajúcich troch veršov adresovaných prorokovi Muhammadovi (p.) spočíva v tom, že proroka nabádajú, aby po skončení svojich každodenných povinností a problémov uvoľnil svoju myseľ, vzpriamil sa a pripravil sa k modlitbe a uctievaniu Pána svojho. Uvedené verše sú síce adresované prorokovi Muhammadovi (p.), avšak ukazujú ostatným veriacim, že by nemali zabúdať na modlenie a na spomínanie si na Boha, keď skončia svoje každodenné povinnosti a mali by si časť svojho dňa vyhradiť na tento účel.")],-1);function T(j,y,f,P,A,S){const a=t("Badge");return n(),r("div",null,[d,e(a,{type:"info",text:"94:1",class:"badge"}),v,e(a,{type:"info",text:"94:2",class:"badge"}),m,e(a,{type:"info",text:"94:3",class:"badge"}),_,e(a,{type:"info",text:"94:4",class:"badge"}),c,e(a,{type:"info",text:"94:5",class:"badge"}),p,e(a,{type:"info",text:"94:6",class:"badge"}),h,e(a,{type:"info",text:"94:7",class:"badge"}),u,e(a,{type:"info",text:"94:8",class:"badge"}),k,b])}const q=s(l,[["render",T]]);export{V as __pageData,q as default};
