import{_ as i,D as t,c as d,I as e,a2 as o,j as s,o as r}from"./chunks/framework.-9P5VKzf.js";const E=JSON.parse(`{"title":"Kur'an","titleTemplate":false,"description":"","frontmatter":{"outline":"deep","title":"Kur'an","titleTemplate":false},"headers":[],"relativePath":"tr/quran/96.md","filePath":"tr/quran/96.md"}`),l={name:"tr/quran/96.md"},v=o('<div class="chapter-title-wrapper"><div class="chapter-title">96</div><div class="chapter-title-slovak">Al Alaq</div><div class="chapter-opening">115</div><div class="chapter-opening-slovak">Rahman ve Rahim olan Allah&#39;ın adıyla</div></div><div class="intro2-wrapper"><div class="chapter-info-wrapper"><div class="chapter-info-translation">Meali</div><div class="chapter-info-name">Diyanet Vakfı Meali</div></div></div><h2 id="_1" tabindex="-1">1 <a class="header-anchor" href="#_1" aria-label="Permalink to &quot;1&quot;">​</a></h2>',3),n=o('<div><div class="main-verse"><h1 class="verse-arabic">ٱقْرَأْ بِٱسْمِ رَبِّكَ ٱلَّذِى خَلَقَ ١</h1></div><p>Čítaj (Muhammad slová Koránu, ktoré ti prinášame) v mene Pána svojho (Boha), ktorý stvoril,</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Tento verš je prvý, ktorým bol posol a prorok Muhammad (p.) oslovený Bohom najvyšším. Je v ňom výzva k čítaniu, písaniu a k vede a vzdelaniu vo všeobecnosti, pretože tak človek dospeje k tomu, aby lepšie vnímal svoje okolie i zákonitosti, ktoré Boh všetkému dal, a tak sa človek aj priblíži k Bohu. Verš hovorí: Čítaj Muhammad tento Korán v mene Boha, ktorý stvoril všetko existujúce. Preto sa každá kapitola v Koráne (okrem jednej), každý úkon, ktorý muslim koná, začína vetou: Rahman ve Rahim olan Allah&#39;ın adıyla, čo znamená, že Boh nielenže má moc milosť udeliť a ju udeľuje, ale táto milosť je v Jeho výlučnej moci.</p></details><div class="break"></div><h2 id="_2" tabindex="-1">2 <a class="header-anchor" href="#_2" aria-label="Permalink to &quot;2&quot;">​</a></h2>',4),_=o('<div><div class="main-verse"><h1 class="verse-arabic">خَلَقَ ٱلْإِنسَـٰنَ مِنْ عَلَقٍ ٢</h1></div><p>Stvoril človeka z pijavice (jedna z etáp vývoja ľudského embrya je štádium, v ktorom sa plod podobá pijavici, kedy sa stočí a cez placentu dostáva živiny z tela matky),</p></div><details class="details custom-block"><summary>Tafsir</summary><p>V niektorých prekladoch Koránu sa slovo Alaq, ktoré sme preložili ako pijavica, prekladá ako zrazenina krvi. Slovo Alaq pripúšťa oba významy, aj keď sa zdá, že preklad tohto slova ako pijavica hlbšie vystihuje podstatu tohto slova. V každom prípade, ak sa na toto štádium vývoja embrya pozrieme, uvidíme veľkú podobnosť s pijavicou i krvnou zrazeninou.</p></details><div class="break"></div><h2 id="_3" tabindex="-1">3 <a class="header-anchor" href="#_3" aria-label="Permalink to &quot;3&quot;">​</a></h2>',4),h=o('<div><div class="main-verse"><h1 class="verse-arabic">ٱقْرَأْ وَرَبُّكَ ٱلْأَكْرَمُ ٣</h1></div><p>Čítaj (Muhammad) a vedz, že Pán tvoj je najštedrejší (vo všetkom, čo dáva),</p></div><div class="break"></div><h2 id="_4" tabindex="-1">4 <a class="header-anchor" href="#_4" aria-label="Permalink to &quot;4&quot;">​</a></h2>',3),c=o('<div><div class="main-verse"><h1 class="verse-arabic">ٱلَّذِى عَلَّمَ بِٱلْقَلَمِ ٤</h1></div><p>Ktorý naučil perom (písať, čím sa poznatky zachovávajú a prenášajú),</p></div><div class="break"></div><h2 id="_5" tabindex="-1">5 <a class="header-anchor" href="#_5" aria-label="Permalink to &quot;5&quot;">​</a></h2>',3),m=o('<div><div class="main-verse"><h1 class="verse-arabic">عَلَّمَ ٱلْإِنسَـٰنَ مَا لَمْ يَعْلَمْ ٥</h1></div><p>Naučil človeka to, čo (človek) nevedel.</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Predchádzajúce verše sa pokladajú za prvé verše, ktoré boli poslovi a prorokovi Muhammadovi vnuknuté Bohom prostredníctvom anjela Gabriela. Bolo to v jaskyni Hirá, kde prorok posledné mesiace, pred zjavením sa anjela Gabriela zvykol tráviť dlhší čas a premýšľať o Bohu. Prvé, o čom verše hovoria, je čítanie a písanie. Zdôrazňujú, že jeden z nespočetných darov Boha najštedrejšieho človeku je pero znázorňujúce písanie. Čítaním a písaním a získavaním poznatkov prostredníctvom toho sa zachovávajú poznatky a informácie nezmenené ľudským podaním a časom. Je to akoby príkaz poslovi a prorokovi Muhammadovi, odkazujúci na dôležitosť tohto prvku ľudskej existencie a vývoja.</p></details><div class="break"></div><h2 id="_6" tabindex="-1">6 <a class="header-anchor" href="#_6" aria-label="Permalink to &quot;6&quot;">​</a></h2>',4),p=o('<div><div class="main-verse"><h1 class="verse-arabic">كَلَّآ إِنَّ ٱلْإِنسَـٰنَ لَيَطْغَىٰ ٦</h1></div><p>Nie (človek sa nespráva tak, ako sa má). Človek prekračuje medze,</p></div><div class="break"></div><h2 id="_7" tabindex="-1">7 <a class="header-anchor" href="#_7" aria-label="Permalink to &quot;7&quot;">​</a></h2>',3),k=o('<div><div class="main-verse"><h1 class="verse-arabic">أَن رَّءَاهُ ٱسْتَغْنَىٰ ٧</h1></div><p>Keď videl (takýto človek sám seba), že nikoho nepotrebuje (že má bohatstvo a moc, tak začne páchať zlo).</p></div><div class="break"></div><h2 id="_8" tabindex="-1">8 <a class="header-anchor" href="#_8" aria-label="Permalink to &quot;8&quot;">​</a></h2>',3),u=o('<div><div class="main-verse"><h1 class="verse-arabic">إِنَّ إِلَىٰ رَبِّكَ ٱلرُّجْعَىٰ ٨</h1></div><p>K pánovi tvojmu bude návrat (po smrti, tak na to nezabúdaj).</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Tento a nasledujúci verš (od 6. až do konca) je adresovaný Abú Žahlovi a ľuďom jemu podobným. Abú Žahľ, ktorý šíril nespravodlivosť a zlo svojím bohatstvom, priveľmi nenávidel proroka Muhammada (p.). Aj keď je tento verš zoslaný v konkrétnej situácii, v jeho obsahu nie je spomenutá konkrétna osoba, čo znamená, že sa vzťahuje na podobné prípady a krivdy vo všeobecnosti a v každom čase.</p></details><div class="break"></div><h2 id="_9" tabindex="-1">9 <a class="header-anchor" href="#_9" aria-label="Permalink to &quot;9&quot;">​</a></h2>',4),b=o('<div><div class="main-verse"><h1 class="verse-arabic">أَرَءَيْتَ ٱلَّذِى يَنْهَىٰ ٩</h1></div><p>Vidíš toho, kto zakazuje</p></div><div class="break"></div><h2 id="_10" tabindex="-1">10 <a class="header-anchor" href="#_10" aria-label="Permalink to &quot;10&quot;">​</a></h2>',3),T=o('<div><div class="main-verse"><h1 class="verse-arabic">عَبْدًا إِذَا صَلَّىٰ ١٠</h1></div><p>Bohu poddanému modliť sa, keď by sa tento modlil?</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Tým, kto zakazuje, sa myslí Abú Žahľ a tým, kto je Bohu poddaný a kto sa modlí, sa myslí prorok Muhammad (p.). Tento verš bol zoslaný potom, ako Abú Žahľ povedal: „Ak by som videl Muhammada modliť sa, stúpim mu na krk“.</p></details><div class="break"></div><h2 id="_11" tabindex="-1">11 <a class="header-anchor" href="#_11" aria-label="Permalink to &quot;11&quot;">​</a></h2>',4),j=o('<div><div class="main-verse"><h1 class="verse-arabic">أَرَءَيْتَ إِن كَانَ عَلَى ٱلْهُدَىٰ ١١</h1></div><p>Vidíš (predstav si Abú Žahľ i každý, kto by podobne konal), ak by bol (Muhammad alebo muslim, ktorý sa modlí) správne usmernený</p></div><div class="break"></div><h2 id="_12" tabindex="-1">12 <a class="header-anchor" href="#_12" aria-label="Permalink to &quot;12&quot;">​</a></h2>',3),y=o('<div><div class="main-verse"><h1 class="verse-arabic">أَوْ أَمَرَ بِٱلتَّقْوَىٰ ١٢</h1></div><p>Alebo by prikázal bohabojnosť (ako bude vyzerať ten, kto by to zakazoval, keď zistí, akej chyby sa dopúšťal)?</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Otázka znie, aký bude osud človeka, ktorý bráni veriacemu v konaní dobra a vo všetkých spomenutých dobrých činoch a v presvedčení a chce ho odvrátiť od cesty pravdy a pokoja. Nech každý zváži následky svojho konania predtým než koná, aby to potom neľutoval, keď mu to už nepomôže.</p></details><div class="break"></div><h2 id="_13" tabindex="-1">13 <a class="header-anchor" href="#_13" aria-label="Permalink to &quot;13&quot;">​</a></h2>',4),A=o('<div><div class="main-verse"><h1 class="verse-arabic">أَرَءَيْتَ إِن كَذَّبَ وَتَوَلَّىٰ ١٣</h1></div><p>Vidíš (Muhammad alebo ty, kto počúvaš, ako by vyzeral Abú Žahľ alebo kto podobne koná), ak by (Abú Žahľ alebo kto podobne koná) za lož označil (Korán a posolstvo, ktorým bol poverený posol Muhammad) a odvrátil sa (od viery a od toho, čo v ňom Boh zoslal)?</p></div><div class="break"></div><h2 id="_14" tabindex="-1">14 <a class="header-anchor" href="#_14" aria-label="Permalink to &quot;14&quot;">​</a></h2>',3),z=o('<div><div class="main-verse"><h1 class="verse-arabic">أَلَمْ يَعْلَم بِأَنَّ ٱللَّهَ يَرَىٰ ١٤</h1></div><p>Nevedel vari, že Boh vidí (všetko, čo on koná a čo v sebe skrýva)? (Skutočne si takýto človek myslí, že Boh ho nevidí a nevie, čo zamýšľa a nechá ho neustále ubližovať veriacim len kvôli ich viere?)</p></div><div class="break"></div><h2 id="_15" tabindex="-1">15 <a class="header-anchor" href="#_15" aria-label="Permalink to &quot;15&quot;">​</a></h2>',3),P=o('<div><div class="main-verse"><h1 class="verse-arabic">كَلَّا لَئِن لَّمْ يَنتَهِ لَنَسْفَعًا بِٱلنَّاصِيَةِ ١٥</h1></div><p>Nie (nebude si robiť, čo chce a ubližovať). Ak neprestane, tak ho potiahneme za šticu (poníženiu ho vystavíme a do pekla silou stiahneme),</p></div><div class="break"></div><h2 id="_16" tabindex="-1">16 <a class="header-anchor" href="#_16" aria-label="Permalink to &quot;16&quot;">​</a></h2>',3),f=o('<div><div class="main-verse"><h1 class="verse-arabic">نَاصِيَةٍ كَـٰذِبَةٍ خَاطِئَةٍ ١٦</h1></div><p>Šticu lživú (lož šíriacu), omylom naplnenú (v omyle a hriešnosti zotrváva a ostatných k nemu vedie).</p></div><div class="break"></div><h2 id="_17" tabindex="-1">17 <a class="header-anchor" href="#_17" aria-label="Permalink to &quot;17&quot;">​</a></h2>',3),S=o('<div><div class="main-verse"><h1 class="verse-arabic">فَلْيَدْعُ نَادِيَهُۥ ١٧</h1></div><p>Nech zavolá tých, s ktorými sedáva (na zhromaždeniach svojich a u ktorých je predpoklad, že ho podporia).</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Ak si myslí, že je taký silný a že ho necháme Muhammadovi ubližovať.</p></details><div class="break"></div><h2 id="_18" tabindex="-1">18 <a class="header-anchor" href="#_18" aria-label="Permalink to &quot;18&quot;">​</a></h2>',4),V=o('<div><div class="main-verse"><h1 class="verse-arabic">سَنَدْعُ ٱلزَّبَانِيَةَ ١٨</h1></div><p>Zavoláme (naňho a na toho, kto s ním príde) anjelov prinášajúcich trápenie.</p></div><details class="details custom-block"><summary>Tafsir</summary><p>Islamskí učenci posledné dva verše dávajú do súvisu s tým, že raz Abú Žahľ prešiel popri prorokovi Muhammadovi (p.), keď sa modlil a povedal mu: „A či som ti nezakázal modliť sa tu, Muhammad?“ Prorok Muhammad (p.) mu nato pohrozil Božou mocou, načo Abú Žahľ povedal: „Čím sa mi vyhrážaš, Muhammad! Prisahám na Boha, že medzi ľuďmi tohto údolia mám najviac spoločníkov a prívržencov“. Nato Boh najmocnejší zoslal oba predchádzajúce verše, ktoré hovoria, že ak by Abú Žahľ zvolal svojich spoločníkov, aby ublížil prorokovi, tak Boh najvyšší svojmu prorokovi a veriacim pošle na pomoc tých najmocnejších anjelov, prinášajúcich so sebou trápenie pre Abú Žahľa a tých, ktorí by s ním boli.</p></details><div class="break"></div><h2 id="_19" tabindex="-1">19 <a class="header-anchor" href="#_19" aria-label="Permalink to &quot;19&quot;">​</a></h2>',4),q=s("div",null,[s("div",{class:"main-verse"},[s("h1",{class:"verse-arabic"},"كَلَّا لَا تُطِعْهُ وَٱسْجُدْ وَٱقْتَرِب ۩ ١٩ ")]),s("p",null,"Nie (veci sa nemajú tak, ako si to predstavuje Abú žahľ). Neposlúchaj ho (Muhammad a pokračuj vo svojich modlitbách) a poklonu sužúd konaj (sužúd je poklona, v ktorej sa človek pokloní, kľakne na kolená, nakloní trup a hlavu smerom k zemi, spojí čelo a nos so zemou a položí ruky ku hlave) a približuj sa (tak k Pánovi svojmu).")],-1),x=s("details",{class:"details custom-block"},[s("summary",null,"Tafsir"),s("p",null,"V jednom z výrokov proroka Muhammada (p.) sa hovorí: „Človek je k svojmu Pánovi najbližšie počas poklony sužúdu“.")],-1);function I(C,N,g,B,R,D){const a=t("Badge");return r(),d("div",null,[v,e(a,{type:"info",text:"96:1",class:"badge"}),n,e(a,{type:"info",text:"96:2",class:"badge"}),_,e(a,{type:"info",text:"96:3",class:"badge"}),h,e(a,{type:"info",text:"96:4",class:"badge"}),c,e(a,{type:"info",text:"96:5",class:"badge"}),m,e(a,{type:"info",text:"96:6",class:"badge"}),p,e(a,{type:"info",text:"96:7",class:"badge"}),k,e(a,{type:"info",text:"96:8",class:"badge"}),u,e(a,{type:"info",text:"96:9",class:"badge"}),b,e(a,{type:"info",text:"96:10",class:"badge"}),T,e(a,{type:"info",text:"96:11",class:"badge"}),j,e(a,{type:"info",text:"96:12",class:"badge"}),y,e(a,{type:"info",text:"96:13",class:"badge"}),A,e(a,{type:"info",text:"96:14",class:"badge"}),z,e(a,{type:"info",text:"96:15",class:"badge"}),P,e(a,{type:"info",text:"96:16",class:"badge"}),f,e(a,{type:"info",text:"96:17",class:"badge"}),S,e(a,{type:"info",text:"96:18",class:"badge"}),V,e(a,{type:"info",text:"96:19",class:"badge"}),q,x])}const K=i(l,[["render",I]]);export{E as __pageData,K as default};
